INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (25, '森林雪－滑雪场施工', '20', '工程', '材设采购', '施工材料,制冰设备', '山东森林雪滑雪设备有限公司专门从事户外运动、旅游、健身等器材的生产销售，主要经营滑雪、滑草、滑水、滑沙的项目施工与运营。在当今中国休闲旅游蓬勃增长、旅游景区竞争异常激烈的时代，专业从事滑草滑雪场的规划设计、施工建设、设备器材的供应和售后服务。公司成功将冬季滑雪场在夏季存在闲置的情况下，将滑草、滑沙、滑水以及漂流等项目成功引入滑雪场，实现滑雪场四季经营，为滑雪场创造全年最大化利润。在做设备供应的同时，公司为投资方提供一站式服务，承接滑雪、滑草、滑沙、滑水市场营销导入与服务工作，创造了滑雪、滑草场年度正式营业一站式服务的成功业绩!公司销售网络已覆盖山东、河南、湖南、浙江、河北、辽宁等地。近年来，公司引入强大的设计团队，为滑雪、滑草场提供规划设计、品牌建设服务。公司始终坚持敬业、协作、学习、创新的质量方针，坚持诚信为本，以顾客满意为第一标准，为客户提供负责的服务，建设创新、环保、具有持续生命力的娱乐项目和优良工程。', '292', '{"id":"id15398546045160","name":"1.jpg","path":"default\\/20181018\\/8b1ace4e98e4fdb7a3c2f31a0ef8e7fa.jpg"}', '山东省', '370100', '165', '1', '0', '1', '山东森林雪滑雪设备有限公司', '{"path":"default\\/20181121\\/f0e450c3dd580c0920c4a4e009cecc07.jpg"}', '平台小助手', '1', null, null, '/supply/25.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (26, '振兴景观15年景观船生产经验', '20', '工程', '材设采购', '游乐设备', '鲨鱼造型海盗景观船海盗船、景观船、观赏木制船用于园林设计中极具观赏性和趣味性景观。设计新颖别致，是各类景观工程标志性的建筑物。景观船不仅可供游人观赏，也可登船游玩，让人在陆地上也能感觉到大海的存在。优质芬兰防腐木：1、选用优质芬兰木，精挑细选确认材质。2、经过除虫、防蚁、防腐，烘干处理后加工成型。3、表面采用优质户外高分子漆喷涂，形成可靠防护层，使其户外使用具有耐候持久，不易破裂、变形、腐烂、虫蛀等优点，历久弥新。4、防腐处理会满刷浸透桐油三遍，待一遍浸透暴晒24小时后，施工一遍，保证木质本色的清漆古朴自然，精工细作，弧度美观，木方厚实，产品质量优，使用寿命长，宜于维护，各种气候适宜，经久耐用，健康卫生。5、线条款式上，符合人体工程力学原理，坐感舒适，造型美观。适用范围：产品适用于公园，地产，商业街，学校，小区，广场，商场，别墅，休闲吧，酒吧，咖啡屋，酒店，度假村，工厂，户外休闲区，公司休闲区，风景旅游区等公共场所。质量：产品均为我厂自己生产，产品质量保证。公司介绍：深圳市宝安区振兴景观设施制造厂创建于1998年，是一家专业生产户外休闲产品的厂家，以拓展户外休闲设施为导向，改善户外休闲环境，将休闲理念带到生活的每个角落。振兴人专业为您提供：垃圾桶、休闲椅、花箱、花架、户外健身器材、儿童游乐架、花园家具、沙滩椅、遮阳伞、果皮箱以及城市景观设施等休闲产品。所有产品其特殊的表面处理，经得起风吹日晒、不易变形、耐磨损、耐腐蚀、使用期在正常情况下达5-10年。并从设计、制造、安装、检验及售后服务均制定了严格的作业程序，竭诚为客户提供优质的产品、周到的服务、从而得到了客户的首肯与喜爱。', '380', '{"id":"216","name":"1.jpg","path":"default\\/20181018\\/32a26a4176a4d2241ffcfa4f55c3c29c.jpg"}', '广东省', '440300', '166', '1', '0', '1', '深圳市宝安区振兴景观设施制造厂', '{"path":"default\\/20181121\\/d043b683122fc865a8bcac618e2db3cb.jpg"}', '平台小助手', '1', null, null, '/supply/26.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (28, '大型公园游艺设施/游乐机械设备/公园游艺机', '20', '运营', '游乐项目', '观光车,碰碰车', '　　江苏省泰州市华凯游乐机械设备厂是游乐机械、游乐设备、游乐玩具、自控飞机、双人飞天、丛林飞鼠、豪华转马、小火车、摇头飞椅、美人鱼、大摆锤、太空漫步、滑行龙、激流勇进、观览风车、海盗船、遨游太空、章鱼、音乐快车、咖啡转转杯、画屏摇摆机、海豚戏水等产品专业生产加工的私营独资企业，公司总部设在江苏省泰州市开发区吴陵南路66号，欢迎各界朋友莅临本公司参观、指导和业务洽谈。', '742', '{"id":"219","name":"1.jpg","path":"supply\\/20180606\\/458a882f961876435e945583336929df.jpg"}', '江苏省', '321200', '168', '1', '0', '1', '江苏省泰州市华凯游乐机械设备厂', '', '平台小助手', '0', null, null, '/supply/28.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (30, '防腐木花箱 碳化木花箱 花架 ', '20', '工程', '材设采购', '施工材料', '　　上海苏誉景观休闲设备有限公司是经国家认证，目前国内最具规模的一家集设计开发、生产制造销售为一体的户外家私专业生产厂家，公司拥有先进的生产技术和完善的工艺流程，产品不但在国内得到客户的好评，还出口多个国家，为国家在该行业的发展中发挥着重要的先锋作用。　　公司产品范围：户外公园休闲椅、城市垃圾桶、花箱、实木组合桌椅、沙滩椅、标识牌、单车架、路边护栏、凉亭小木屋、葡萄廊架、健身游乐设施、防腐木小品等一系列城市园林设施。　　我们一直在努力，不敢有丝毫的懈怠，每一次成功都是我们团队一个新的起点。我们会经常地告诫自己“品质来自创新，信誉铸就辉煌”。将努力为大家打造现代的健康的城市园林生活。', '690', '{"id":"226","name":"4.jpg","path":"supply\\/20180606\\/b49febf785eecf764b198ef024c38e23.jpg"}', '上海市', '0', '170', '1', '0', '1', '上海苏誉景观休闲设备有限公司', '{"path":"default\\/20181121\\/78dc9ad772d218edb274ad759738cd6a.jpg"}', '平台小助手', '1', null, null, '/supply/30.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (31, '古建工程 设计 施工 养护专业服务', '20', '工程', '施工', '景观工程,房建工程', '河北香山园林古建筑工程有限公司，始建于1998年4月，企业原名为河北香山园林古建筑工程公司，于2007年8月注册为河北香山园林古建筑工程有限公司，是国家建设部核准的园林古建筑一级企业,公司共设9个工程项目部，现有12个园林古建工程队。公司注册资金5215万元，技术力量雄厚，专业设备配备齐全;现有各类专业技术人员102人(其中国家注册高级景观设计师2人，国家注册古建营造师6人，高级工程师6人，工程师39人)。可承揽古建筑施工、古建文物修缮、复建工程、仿古园林建筑施工；园林假山、驳岸叠石工程和铜雕、石雕、木雕、彩塑、古建油漆彩绘、园林绿化工程，程控音乐喷泉设备制作安装。公司自组建以来，竣工项目遍布北京、内蒙、河北、河南、山东、山西、江苏等地;仿古建筑、园林绿化、喷泉等遍布全国各地；部分项目还跨出国门，进入俄罗斯市场，为公司创下丰硕成果，实现年生产能力8500万的辉煌成果，赢得了广大业主和用户的赞誉。公司以质量求生存，以诚信谋发展，近几年来，公司共获得园林古建景观工程和水景景观工程省市级九项大奖，并荣获国家知识产权局颁发的水景专利证书和保定市工商行政管理局颁发的“河北省工商企业信用AAA级,守合同、重信用单位”荣誉证书。北香山园林古建筑工程有限公司坚持“立足京冀、多元经营，面向全国”的经营理念，以“展现一流技术，提供一流服务，建设一流工程，开创一流业绩”的时代精神，奉行“诚信为本、信誉至上、追求卓越、服务社会、创精品工程”的经营宗旨，严格管理，规范施工，以“质量好、信誉高、速度快”的企业风貌，竭诚为社会各界提供优质服务。', '638', '{"id":"224","name":"4.jpg","path":"supply\\/20180606\\/8bd34458015022c5d14fcbaadae59fb1.jpg"}', '河北省', '130600', '171', '1', '0', '1', '河北香山园林古建筑工程有限公司', '{"path":"default\\/20181121\\/2780d50c85bbf5a7b6b3cd2b42d1d9ac.jpg"}', '平台小助手', '1', null, null, '/supply/31.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (32, '全球滑雪场设计 世界顶级山地规划公司', '20', '规划设计', '设计', '景观设计', '　　北京双威力冰雪设备有限公司是一间致力于为中国滑雪场、滑冰场提供一流的冬季运动设备、滑雪场规划设计的资深服务商。　　双威力核心优势是：　　专心---十五年专注一件事，最了解客户需求　　双威力自1996年进入中国后，始终置身于体育、旅游领域冰雪行业发展的前沿，最了解真实的行业资讯信息，深悟中国滑雪场发展路程。经历了一百多个项目的历练，使我们对滑雪产业的相关问题拥有广泛而深入的认知;对不同客户个性化需求能提供其整体解决方案，为专属资源的您打造更具前景的旅游优化发展模式。　　专业---以国际化的设计观和管理理念享誉业界　　双威力紧紧依托世界冰雪行业四大品牌公司，组成团队。总代理加拿大庞巴迪公司、美国SMI造雪工程公司、意大利PRINOTH压雪机公司的产品。并且和加拿大Ecosign山地规划设计公司有多年的合作。多层面的第一品牌专家团队，使我们在选择合作伙伴之初，就奠定了世界技术领先，项目运作经验丰富的基础。这些专家集群形成一个庞大的智力平台，引领世界滑雪行业潮流。再通过双威力的消化、吸收和本土化运作，定可使您获得意外的收获。　　高品质---世界冬奥会的选择　　历史上，双威力总代理的产品曾多次是世界大赛、奥运会官方指定设备。而今又或殊荣，成功站在了世界滑雪设备的顶峰，再次成为2010年温哥华世界冬奥会造雪系统设计者、官方指定造雪系统集成商。2014年俄罗斯索契世界冬奥会造雪系统设计者、官方指定造雪系统集成商。　　高性价比—-客户满意的关键　　我们产品是高效率，低成本，高回报的造雪系统。它能使您的雪场开业早，歇业晚，延长经营时间，增加营业收入;能协助您减少成本，降低经营风险。产品已覆盖全国17个省、市、自治区，为一百几十个雪场、冰场用户提供设备或完成交钥匙工程，得到冰雪行业用户广泛认可。　　成为国家体育场(鸟巢)的造雪系统的设备供应商。　　成为中国两次举办亚冬会——第三届(亚布力)、第六届(北大湖)亚冬会官方指定造雪系统设备供应商、服务商。　　成为中国首次承办世界大学生冬运会—24届大学生冬季运动会官方指定造雪设备供应商、服务商。　　成为亚洲首届雪地马球挑战赛——中国高银杯国际马球挑战赛设备供应商、服务商。', '373', '{"id":"228","name":"1.jpg","path":"default\\/20181101\\/ef5bdb1f707fddb35ba324519b37b8a4.jpg"}', '北京市', '0', '172', '1', '0', '1', '北京双威力冰雪设备有限公司', '{"path":"default\\/20181121\\/50305569468bbbfb1728da8462f2a597.jpg"}', '平台小助手', '1', null, null, '/supply/32.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (33, '北京绿普景观花卉', '20', '规划设计', '设计', '景观设计', '北京绿普方圆花卉科技有限公司成立于2004年，是集生产销售和种植养护与一体的景观花卉专业供应商。目前公司拥有生产基地300余亩，年产地被宿根花卉已超千万株。我公司与在京科研院所建立了良好的合作关系，从花卉品种选育、新产品信息收集等方面有着独特的资源优势，尤其是多年生宿根地被花卉的扩繁增量方面我们探索的整套方案均得到业内专家的一致好评，中央电视台“科技苑”栏目组也专门对我公司宿根花卉生产情况进行了专题报道。在公司发展进程中，我们秉承质量第一、客户至上的经营理念不断创新、锐意进取，其间经历了北京奥运工程、滨海新区达沃斯会议、天津津南大学城、东方园林重点项目以及房地产花卉造景等项目的花卉供应任务。我公司突出一站式服务，从指导甲方花卉品种选择、设计方花卉习性把握到施工方花卉打包批量供应提供全程顾问式解决方案，客户对我公司的产品质量、物流配送、技术指导、时尚引领等方面都给予了高度赞扬。眼下，在各级政府积极倡导低碳、节能、环保理念，努力推动自然、生态项目发展，下大力气控制PM2.5之际。我公司再度启航，瞄准市场脉搏，选择了一大批节水、耐旱、耐瘠薄、越冬效果好、管理粗放的宿根地被花卉以供广大客户朋友选用。热忱欢迎各界朋友前来我公司基地参观指导，并期盼与您合作愉快！', '296', '{"id":"229","name":"1.jpg","path":"supply\\/20180606\\/0e19cd1108af8487b834d4cc6166b32c.jpg"}', '北京市', '0', '173', '1', '0', '2', '北京绿普方圆花卉科技有限公司', '{"path":"default\\/20181121\\/2764f7702d73dc8d5587f8213786b4b7.jpg"}', '平台小助手', '1', null, null, '/supply/33.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (34, '景观园林施工龙日园艺景观', '20', '规划设计', '设计', '景观设计', '　　深圳市龙日园艺景观有限公司是一家融园林景观设计、施工、养护、苗木产销为一体的规模化园林景观综合企业。在主题公园、旅游园林、地产园林等市场领域，形成“创意设计、景观施工、苗木假植、生态林业”组成的“四位一体”核心服务体系架构。　　公司先后通过了ISO9001质量管理体系认证、ISO14001国际环境管理体系认证及OHSAS18001职业健康安全管理体系认证;先后获得了“全国建设信用AAA级诚信企业”、“全国园林绿化质量管理优秀企业”、“全国政府放心、用户满意先进单位”、“深圳管养优良样板金奖”等荣誉，并荣膺成为广东省市风景风景园林协会单位会员、深圳市风景园林协会常务理事单位。　　公司秉承“资本化运作，品牌化经营，国际化产业”的经营理念，不断完善管理结构，开拓探索创新技术，逐步建立起了现代化企业管理体系，不仅拥有一支战斗力强，专业素质高，超过400人的设计、施工管理团队；更分别在北京、天津、上海、重庆、西安、成都、武汉、江西、广东、广西、云南、海南等城市设立分支机构。　　经过数十年的悉心经营，公司已从景观园林施工领域的领先者逐步发展成为景观园林设计施工一体化的行业龙头企业。在未来的发展中，龙日人将一如既往，务实前行。　　业务介绍：　　【创意设计】　　公司聘请美国资深设计师BrocSmith作为首席设计师，并拥有一支专业的设计团队，能够为您提供优秀的环境景观和绿化种植设计，包括从方案到施工图的一系列设计工作。同时，设计人员对工程的施工进行全程跟踪，由首席设计师把控最终效果，保证工程能高质量完成。　　【景观施工】　　我司有多年从事园林绿化施工和养护的经验，术力量雄厚，机械设备齐全，苗木资源丰富，在北京和成都均拥有过百亩的苗木基地。我司绿化施工和养护力，承接各类园林绿化工程，包括公园绿化、小区绿化、校园或厂区绿化，以及市政绿化。我们以专业的技艺，诚信的态度竭诚为您服务。　　【苗木基地】　　公司将展开东西南北中的苗木基地布局，掌握第一手的苗木资源。并整合资源优势，创建大树网——中国最专业，从事大树交易的电子商务平台。综合利用苗木资源优势，结合大树网，发展苗木配套业务，3年内实现各地苗圃基地，总面积达到2万亩的目标。　　【生态林业】　　近年来，公司致力于生态林业的发展，在各地苗木基地基础上发展生态林业，并且有效的提高了经济效益、生态效益及社会效益。　　公司以自身苗木资源优势，促进生态林业板块业务发展，两年内在江西萍乡市湘东区麻山镇汶泉村实现建成林场2万亩，主要栽植各种常用景观用苗木，为各地苗圃提供资源支持。并且积极同国家林业科研机构合作，种植白木通与拐枣树(国家重点扶持的有机食用油原料项目)，增加林地附加值，改善生态环境。', '748', '{"id":"232","name":"1.jpg","path":"default\\/20181101\\/c2ac82fd5ee49d029ab64f0ed80757aa.jpg"}', '广东省', '440300', '174', '1', '0', '1', '深圳市龙日园艺景观有限公司', '{"path":"default\\/20181225\\/602678ea61babf756055dc2674ca48c6.jpg"}', '平台小助手', '1', null, null, '/supply/34.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (35, '专业从事建筑亮化照明工程', '20', '工程', '专业分包', '照明', '北京博雅特照明科技有限责任公司是一家以高新科技为主的多元化企业，专业从事楼体亮化、照明、户外标识制作、施工企业，承接各种LED亮化工程(LED显示屏、LED标识、LED亚克力产品)及楼宇监控工程(楼宇对讲、综合布线、安防工程)。公司拥有先进的设备和一批具有多年施工经验的专业设计师与工程技术人员，经过大家共同努力赢得客户的认可和广泛的赞誉！我们始终坚持以质量求生存，以信誉求发展的经营理念，严格按照ISO9001质量管理体系运做，为社会奉献了大量精品工程,赢得了客户的广泛赞誉。我公司下设业务部、设计部、办公室、生产制作部、安装工程部、维修部等部门。经过长期的发展，已积累了丰富的户外各种媒体广告制作的经验，拥有了一批丰富经验的设计及工程技术、工程制作、工程安装人员，组成了一条为客户提供贯穿全程的服务体系。凭借现代化的企业管理手段与优秀高效的团队，不断发挥自身优势和整合行业资源，利用丰富的技术经验，向客户提供专业的资讯信息，为您推荐最适合您的工程方案及完整的图纸化全方位解决方案。我们不是虚有其表，我们是实干的专家。我们不断努力，争取永不停歇让进步永无止境。我门的设计师与技术人员每天都一起工作，一点一滴、精雕细啄地满足您的每一个要求，及时，高效，并且控制在您的预算之内。我们愿与您一起携手并进，共创辉煌！', '743', '{"id":"234","name":"1.jpg","path":"default\\/20181101\\/6d666b4d39dcec286b4c700a5c5a52e9.jpg"}', '北京市', '0', '175', '1', '0', '1', '北京博雅特照明科技有限责任公司', '{"path":"default\\/20181121\\/d1a44588c9e510b44dcdaf6840029a2c.jpg"}', '平台小助手', '1', null, null, '/supply/35.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (36, '恒达自动感应讲解器', '20', '互联网+', '同城平台', '', '　　公司介绍：　　天津恒达公司坐落于天津滨海高新技术产业园区，是专业开发、生产、销售语音讲解设备的高新技术企业。在北京设立有分公司，在广州、上海设立了办事处。公司聚集了100多位从国内名牌高校走出的尖端人才。　　自1999年中国第一台语音导览机在天津恒达公司诞生以来，10年间，恒达人本着“开拓进取，务实创新”的精神，自主研发了包括语音导讲系统、无线讲解系统、同声传译系统在内的，适合各级、各类博物馆及相关行业的20多个产品系列，为全国几百家用户提供并实施了个性化的解决方案。凭借国际领先的技术水平，恒达公司已成为行业发展的领跑者。　　恒达人秉承“诚信做生意，诚心做朋友”的经营理念，从前期的实地调研、方案制作、产品生产，到后期的语音制作、技术培训、售后服务，每一个环节都注重品质。正是凭借雄厚的技术实力和生产能力，以及全方位的优质服务，我们得到了用户的广泛赞誉。全国各大省级博物馆，国家一号工程项目，国家4A、5A级景区等1000多家文博单位，相继成为恒达公司的尊贵用户。长久以来，恒达公司的产品一直保持国内市场占有率90%以上，并已走出国门，远销海外。　　2010年，恒达公司被评定为天津市滨海新区高新技术企业，恒达多媒体导讲机被认定为天津市自主创新产品。截至目前，恒达产品已获得12项国家级技术专利，其中多项技术领先于国际水平，填补了行业领域的空白。　　作为导讲设备民族品牌的引领者，天津恒达公司始终富有一种使命感——用科技弘扬科学文化、传播中华文明。　　产品介绍：　　自动感应广播式讲解；音量无级可调；感应角度：≤120°；感应半径：≤6m。适用于纪念馆、博物馆、展览馆、陈列馆、旅游景点等需要固定讲解的场所；人到声起，人离声息；体形轻巧、美观大方、音质清晰、安全节能。', '326', '{"id":"240","name":"1.jpg","path":"default\\/20181101\\/8507116922dbc6ab5bb1c1ac5c301b14.jpg"}', '天津市', '0', '176', '1', '0', '0', '天津恒达文博科技股份有限公司', '{"path":"default\\/20181121\\/0adb6441cb795288dc25c2a93e0d5f0d.jpg"}', '平台小助手', '1', null, null, '/supply/36.html');
