INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (46, '专业湖面喷泉', '20', '运营', '演艺', '水景秀', '　　水幕电影是通过高压水泵和特制水幕发生器，将水自下而上，高速喷出，雾化后形成扇形“银幕”，由专用放映机将特制的录影带投射在“银幕”上，形成水幕电影。当观众在观摩电影时，扇形水幕与自然夜空融为一体，当人物出入画面时，好似人物腾起飞向天空或自天而降，产生一种虚无缥缈和梦幻的感觉，令人神往。水幕电影投影机由机械装置、控制支架、通讯口、软件、时间信号界面及DMX512接口组成。　　该投影机的发动机通过光学传感控制，精度高，其控制方法有三种：编程控制、直接控制和实用程序控制。水幕高达20余米，宽30-50米，各种VCD光盘或水幕专用影片均可在水幕上播放，影视效果奇特、新颖并是极佳的广告宣传工具，各种广场及阔旷的水面均可安装水幕电影。　　河北金如意音乐喷泉有限公司位于巍巍太行山下，伟大的白求恩主义精神发祥地，革命老区——河北省唐县。公司是以大中型综合水景音乐喷泉业务为主体，集喷泉，假山，不锈钢雕塑，石雕，铜雕，园林古建等为一体的大型园林企业集团。主要服务于市政建设、园林绿化,以及工厂、学校的环境美化。公司下设喷泉工程事业部，雕塑工程事业部，园林古建工程事业部。公司拥高水准的设计，研发，生产和施工人员。　　河北金如意音乐喷泉有限公司自成立以来始终坚信科技是第一生产力，以优质诚信为根本，不断创新。其中在喷泉领域已达国内领先行列，主要产品有：音乐喷泉、旱式喷泉、程控喷泉、室内喷泉、冷雾喷泉、光亮跳泉、激光水幕电影等系列。公司成立以来奉行品牌战略，在严格要求各个部门服务标准的同时，积极进行技术改进和创新，做到经手的就是精品工程。凭借良好的商业信誉和绝对的质量保证以及完善的售后服务得到了全国各地众多客户的高度评价和赞誉,在客户中建立了良好的口碑，赢得了客户的信任。　　河北金如意音乐喷泉有限公司经营方针：勤奋敬业，诚实奉献;科技先锋，行业翘楚；礼义智信，以人为本；踏实严谨，快速高效，美化家园，造福社会，是我们公司的宗旨和责任。我们将尽最大的努力，期待与您携手共创新的辉煌！', '214', '{"id":"257","name":"23.jpg","path":"supply\\/20180606\\/30625a24c314d39029d0b5c61d178a85.jpg"}', '河北省', '130600', '186', '1', '0', '7', '河北锦泉园林景观工程股份有限公司', '{"path":"default\\/20181121\\/dcdc35846f26eff5e1960105b42e0bd2.jpg"}', '平台小助手', '1', null, null, '/supply/46.html');
