INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (79, '北京中轴线5日——徒步中轴线，一品北京味', '20', '运营', '住宿', '研学', '课程一：钟鼓楼——“中轴龙脉，晨钟暮鼓”击鼓定更，撞钟报时，是关于北京的记忆，伫立在中轴线北端的钟楼和鼓楼是元、明、清三代都城的报时中心，七百年来见证着岁月的沧桑。课程内容：（1）军训开营：营员、辅导员、教官互相认识，在钟鼓楼之间的广场举行简单的开营、宣誓、破冰、分组活动；（2）击鼓表演：欣赏钟楼击鼓表演，感受雄浑鼓声；（3）凭楼远眺：远眺中轴线，教官下达上午的课程内容。课程二：非遗体验课——串糖葫芦“嘿，冰糖葫芦哟，新蘸的。”老北京的胡同中，走街串巷的老人挑着担子或挎着木提盒，一边走一边吆喝，这是老北京的一道靓丽风景线。“都说冰糖葫芦儿酸，酸里面它裹着甜……”，糖葫芦这种传统的小吃正逐渐淡出现代孩子们的视线，北京的味道，就藏在一颗颗晶莹剔透的糖葫芦中间。课程内容：（1）京味遗韵：邀请糖葫芦制作老师现场带领孩子制作糖葫芦，体验动手的乐趣；（2）北京吆喝：老北京吆喝是北京市的传统民俗文化之一，学生学习老北京的吆喝，试着售卖自己制作的糖葫芦。课程三：景山——抖空竹景山，西临北海，南望故宫，是明清两代的御苑。由于景山的位置刚好处在北京城的中轴线上，又是皇宫北边的一道屏障，因此又被称为“镇山”。课程内容：（1）小组PK：学生以小组为单位，完成指定的课程任务，看看哪一组最先完成任务，抵达山顶；（2）抖舞空竹：“一声低来一声高，嘹亮声音透碧宵”，空竹——老北京的声音，国家级非物质文化遗产。玩儿空竹的主儿不仅能把空竹抖得山响，还可以把空竹抛向天空，做出“骗马”、“卧鱼”、“黄瓜架”、“风摆荷叶”的姿势来，老艺人与小孩子在景山顶上抖起空竹，在飞舞的空竹中体味老北京。课程四：永定门——分享我与北京城的故事两天的徒步北京之旅，营员们终于经历了种种考验，抵达行程的最后一站——永定门。回望走过的路，营员们收获了一路的欢声笑语，一群可敬可爱的大哥哥大姐姐，一段将北京味道镌刻在记忆深处的穿越之旅——非遗、胡同、小吃、叫卖、糖葫芦、大碗茶、抖空竹……最后，小营员、辅导员、教官共同相聚于永定门，胜利会师，进行即兴的团队庆祝游戏，分享我与北京之间的故事。', '660', '{"id":"id15397581463180","name":"timg.jpg","path":"default\\/20181017\\/ccbd371a32a06b02d04ea87f16b7a9af.jpg"}', '北京市', '0', '293', '1', '0', '1', '世纪明德', '', '平台小助手', '0', null, null, '/supply/79.html');
