INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (103, '景区智慧交通运营服务（观光车、接驳车、直通车）', '142', '运营', '游乐项目', '观光车', '   智慧交通运营系统是广东畅享游耕耘景区运营多年经验打造的新型系统。主要是通过利用纯电动绿色能源客车建设车辆管理、车辆调度、车辆充电、车辆告警、智能停车模块实现景区内外互联相通，为游客带来出行便利的同时也契合绿色、环保理念。智慧交通系统真正的实现了交通管理的动态化、全局化、自动化、智能化。畅享游的优势：车辆优点：新能源汽车、静音、无尾气污染、全电子化、简单、高效、能源利用率高、扭矩大、爬坡强。成熟产品：景区观光车、旅游直通车、车辆租赁。强大的技术平台：微信小程序、微信服务号、后台管理系统、平板APP。成功经验：流溪河森林公园，五指山景区，石门国家森林公园。众多合作企业：卓思道温泉、耕山小寨、香蜜山、碧水湾温泉、港中旅等20多个企业。畅享游智慧交通系统部分功能优势：车辆调度传统车辆调度：人为修改车辆和调度车辆、排班信息需面对面告知司机，人员对车辆和司机调度容易出错，投入人力资源大。畅享游车辆调度：基于移动互联网、只需车队长或调度员即可安排司机排班、经过车辆调度功能即可将车辆分配到相应的司机、分配车辆后司机收到短信同时LED信息发布屏显示排班信息、能将已分组的车辆或者未分组的车辆分配到不同的分组根据分组不同改变车辆的归属部门及车辆行驶的范围，车辆调度系统充分利用互联网优势，减少人力资源投入，节约调度车辆成本，总体提高公司管理水平和赢利能力。车辆监控传统车辆监控： 无法获取车辆闲置天数、日里程、离线、地图位置、原点距离等信息，管理者必须电脑登录登录才查到车辆数据。畅享游车辆监控：实时展示每辆车信息，定时刷新得到车辆编组、电量、状态、耗电量、位置、离线、闲置等情况，并支持电脑、微信、app实时查看，可以有效的实现远程监控。我要乘车传统乘车系统：目前大部分景区没有内部的车辆，也没有规划准确的游玩站点，游客并不知道自己具体位置及车辆到在哪，没有良好的游客体验，降低了游玩乐趣。我要乘车系统：专门针对不同景区打造不同的的站点路线，根据车辆行驶或启动状态显示在路线上，根据用户处的位置，点击每辆车展现不同的提示信息，方便用户查看自己的实时位置和站点，同时可以看到行驶车辆还有多少分钟到达接送点，大大增加用户体验。', '176', '{"id":"id15319794221150","name":"2.jpg","path":"default\\/20180719\\/eddf6de6ed39d5671e8a504a357ef1b8.jpg"}', '广东省', '440100', '540', '1', '0', '1', '广东畅享游科技有限公司', '{"path":"default\\/20181227\\/11984ebea58f7aa3f993d017006abacc.jpg"}', '关安耀', '1', null, null, '/supply/103.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (104, '致力于古建筑规划设计服务', '143', '规划设计', '设计', '建筑设计', '　　北京绿维古汉奎智规划院能提供各类旅游策划规划设计，对古建筑规划设计有独到之处。　　成功案例有湖北荆州章华寺、四祖寺、五祖寺；策划规划实施了广西容县真武阁景区、广西岑溪东山公园、陕西山阳县漫川关古镇、南宁市国家级田园综合体美丽南方历史文化区、江西省修水县茶文化观光园等等。　　北京绿维古汉奎智规划院是经验丰富、业绩满满的实力强大的规划设计机构，欢迎广大客户来公司参观考察。', '135', '{"id":"id15396810936225","name":"\\u5fae\\u4fe1\\u56fe\\u7247_20181016171037.png","path":"default\\/20181016\\/cb77e04b308f7c8093814d54efe605cb.png"}', '湖北省', '420100', '541', '1', '0', '1', '北京绿维古汉奎智分院', '', '吴柏胜', '0', null, null, '/supply/104.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (105, '旅游规划4.0新模式的开创者', '94', '规划设计', '规划', '发展规划,专项规划,总规', '　　旅游规划4.0新模式的主要特征：　　一、精准定位、为项目找魂。　　定位是项目的灵魂，定位解决项目的特色和差异化。　　二、看图说话，不说废话。　　陈慕挺带领的实战规划团队，打破以文字描述、平面图件、百度图片为主的规划旧模式，改为“看图说话”。　　三、精彩展现、创新汇报。　　陈慕挺教授团队进行了规划汇报方式的创新，用通俗易懂、直观明了、声情并茂、图音并茂、创意+视频+音频的“多媒体”形式进行汇报。　　四、资源变资本。　　通过精准定位、高水平策划规划+多媒体精彩展现，原本平平常常的资源立马转化为高价值资本，就有资格去引资：　　1、价值提升：精准定位把项目的文化灵魂展现得淋漓尽致，成千上万倍地提升项目地块开发的价值，把一块普通的土地变魔术般的变成投资旺地。　　2、锁定开发权：能让政府领导和专家对规划方案高度认同，快速通过评审，为开发商节约宝贵的前期运作时间，早日锁定项目开发权。　　3、身份转变：把一个普通的开发商推上了运营商的高度，从自己单打独斗变成资源整合者、项目整体运营商、资本运作平台主导者。　　4、营销宣传：项目在运作过程中即可利用多媒体进行营销，扩大项目影响力和知名度，为景区成功开园奠定坚实的人气基础。　　五、吸引投资。　　陈慕挺教授创新的4.0规划模式具有以上强大的功能，加上规划多媒体本身就是极好的招商工具，其他投资商通过多媒体看懂了项目的开发价值，主动入股项目或直接收购项目开发权，为项目聚集更多的资金和能量，项目成功在望。　　旅游规划4.0模式总结：　　一、能够成百上千倍地提升项目价值，把一块普通的土地变成投资旺地;二、能让政府领导和专家对规划方案增加认同，快速通过评审，为开发商节约宝贵的前期运作时间，早日锁定项目开发权;　　三、把开发商推上了运营商的高度，从自己单打独斗变成资源整合者、项目整体运营商、资本运作平台主导者;　　四、未开园就能进行营销宣传，提前预热，扩大知名度和影响力;　　五、辅助项目投融资：优秀项目能够吸引投资，政府的政策性资金向项目倾斜、其他战略合作伙伴和中小投资商加盟、基金接入等，从而使项目聚集大量的资金，更快建设、开园、成功。', '128', '{"id":"id15396805587315","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810161701093.png","path":"default\\/20181016\\/9bdbf9947aa641842d1d3c23bb7142ab.png"}', '贵州省', '520100', '545', '1', '0', '1', '贵州绿维创景规划设计院有限公司', '{"path":"default\\/20181207\\/7422eeabcfca9aaa41defd69f5e21ede.jpg"}', '陈慕挺', '1', null, null, '/supply/105.html');
