INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (111, '提供专业的目的地营销服务', '90', '运营', '营销', '品牌营销,渠道营销', '　　目的地营销是为了完善旅游产业服务链，结合当前国内外目的地旅游产业发展需求，而提供的专注于旅游目的地整合营销的服务。　　我们团队拥有丰富的目的地客户服务以及项目策划执行的经验，能够应对复杂的需求环境，为国内外政府旅游管理机构、旅游企业、旅游景区提供专业的旅游目的地品牌咨询、策划、营销推广、落地执行以及产品设计、渠道建设、专家智库等全方位的目的地营销解决方案。包括旅游城市营销、景区营销、乡村旅游营销等!', '148', '{"id":"id15396802237810","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810161653048.png","path":"default\\/20181016\\/4d8864b3073d6b3f479bf1b336c1ed4b.png"}', '陕西省', '610100', '566', '1', '0', '1', '陕西绿景旅游规划设计有限公司', '{"path":"default\\/20181016\\/079a0c8becd34d8b0b0a73d40e700e8f.png"}', '李伟明', '1', null, null, '/supply/111.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (112, '佛文化主题旅游景区或者城市的主题项目策划、规划、制作等服务', '129', '规划设计', '策划', '产品策划', '　　我司提供佛文化主题旅游景区或者城市的主题项目策划、规划、制作服务，其中包括演艺、学堂、流通中心等。　　中国领先的佛文化主题旅游服务业内容提供商与运营商，以特色精神信仰为核心内容、以旅游场景为主导方向、增加高科技现代化视觉工程的运用，创作具有传播性与欣赏性的视听盛宴。金冠文旅具有大型佛文化主题旅游产品的综合实现与运营管理能力，并且常年为高端旅游景区提供文化创意、主题设计、科技开发等全产业链整合方案服务。　　我们的核心业务始终关注旅游文化产品的核心价值观树立与实施管理，包括音乐会、文化节、佛教纪念日活动、体验馆、主题酒店、文化衍生产品设计制造等多个业务板块，以佛文化旅游项目的建设和实现为平台，不断提供丰富的主题文化内容和创意支持，专注旅游产品的文化主题演绎。　　主要工作流程：　　我们将根据客户提供的项目需求进行两到三次实地考察，与客户进行深度沟通，挖掘当地相关文化历史 
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (113, '大型文化活动策划服务', '129', '运营', '演艺', '灯光秀,水景秀,实景演出,夜游演出', '　　金冠文旅演艺中心是由长期从事旅游行业运营、大型文化活动策划执行的资深专家组成，团队核心成员均参与过CCTV大型文艺晚会制作、各地主题文艺晚会制作等，部分成员来自著名的中国山水实景演出制作公司。金冠文旅演艺制作团队实战经验丰富、跨界整合能力强。　　多年来参与的大型演出、展览活动累积约100场。以弘扬传统文化为宗旨，将各地悠久历史与演绎结合，从惠民工程到高端人群，策划不同规模与形式的文艺活动与会展，其中《非遗夜宴》旅游文化节入选民贸会战略合作单位，将成为四部委签发的“圆梦广场”精准扶贫项目制作单位。随着市场环境变化，金冠文旅拓展文娱市场，创立起3D全息剧院项目，适合亲子主题乐园、都市小剧场演出。目前已经在杭州演出，受到观众追捧。　　金冠文旅的优势在于策划、规划、创作、制作、营销一站式服务体系，并且是资深专家团队操盘，不仅保证活动质量，更注重品牌的影响力和美誉度。　　成功案例：　　1、《非遗夜宴》民族旅游文化节　　2、《黄河大典》旅游文化节　　3、北京凤凰岭“亲子音乐节拍嘉年华”　　4、青岛“海上嘉年华”音乐节　　5、青岛金海滩旅游文化节　　6、辽源《国际琵琶艺术节》　　7、敦煌飞天音乐节　　8、迁西房车露营小镇青春派轰趴音乐节　　9、现代京剧经典唱段音乐会西北红色之旅巡演　　10、荆州《刘备招亲》实景演出创制作　　11、荆州古城旅游规划及“入城仪式”创制作', '119', '{"id":"id15396783684982","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810161625471.jpg","path":"default\\/20181016\\/29277f63fecf27f9d8577e202375c0ac.jpg"}', '北京市', '0', '527', '1', '0', '1', '金冠伟业（北京）文化发展有限公司', '{"path":"default\\/20180723\\/04d075ea3d13967a447babe2904903c7.jpg"}', '王莉莉', '1', null, null, '/supply/113.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (114, '温泉洗浴设计、施工、运营、管理一站式服务', '170', '规划设计', '设计', '景观设计,建筑设计,装修设计', '　　随着温泉旅游的迅速升温，人们对温泉的需求远远不止于沐浴、康体等温泉产品，而是追求产品多元化体验，尤其在自然资源一般的温泉度假地，仅靠单一的温泉资源开发不足以支撑度假区的有效发展，需要多元的产品来增强温泉地的吸引力和竞争力，这一现状就推动了综合式温泉开发模式的产生。　　美景充分利用温泉水资源的优势与生态环境，结合养生、休闲、观赏、户外活动、旅游地产和养老，在开发洗浴温泉旅游度假时满足开发商的实际需求和基本利益，同时满足当地政府及政策要求。通过多年的实操经验，总结出成功的开发模式：温泉综合体。　　与以往的大众温泉洗浴开发方式不同，虽然温泉水仍然是主要的核心吸引资源，但高端精品温泉的打造赋予了温泉度假更多的内涵，高端的服务、精美的设计、优越的环境、超然的享受，养生、度假、文化体验等多种体验方式和温泉融为一体已经成为现阶段温泉旅游的一个重要消费理念，其最大的特点是温泉与SPA、文化内涵的紧密结合，提供高档次、专业化的服务项目，以“精致型、小型化、私密性”的方式经营，使游客在尊享高端温泉度假产品的过程中，达到身心的彻底放松和享受，打造一种全新的温泉休闲度假方式体验，即温泉生活方式，已经成为热潮。　　专业设计团队，丰富的温泉洗浴装修经验，免费量尺，免费设计，只为达到客户满意。', '98', '{"id":"id15396764685595","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810161553475.png","path":"default\\/20181016\\/acfbdadea17bfff6aae38c40f09907c5.png"}', '辽宁省', '210100', '567', '1', '0', '1', '沈阳美景环境艺术工程有限公司', '{"path":"default\\/20180723\\/eef78ad6732211c7f8f0181428af8cd6.jpg"}', '王强', '1', null, null, '/supply/114.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (115, '城市规划设计一站式服务', '170', '规划设计', '规划', '专项规划', '　　美化环境，改善人类生存空间的质量，创造人与自然、人与人之间的和谐是景观设计的最终目的也是最重要的作用。美景景观设计的目的是通过某种创意形象来揭示人类社会和自然中的本职和规律。美化环境并且能够促进人和自然以及人和人之间的和谐相处，从而创造可持续发展的环境文化。　　沈阳美景集团专业园林——景观设计，从事城市规划设计一站式服务，美景集团在景观设计领域深耕10余年，拥有专业景观设计师，专家学者，以高品质的景观设计项目赢得用户信赖。', '91', '{"id":"id15396765795352","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810161556021.png","path":"default\\/20181016\\/509a38fd552378c87d8ac3a0d482372a.png"}', '辽宁省', '210100', '567', '1', '0', '1', '沈阳美景环境艺术工程有限公司', '{"path":"default\\/20180723\\/eef78ad6732211c7f8f0181428af8cd6.jpg"}', '王强', '1', null, null, '/supply/115.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (116, '欢迎加入辽宁省工商联文化旅游协会', '170', '泛旅游', '文化', '', '　　辽宁省工商联文化旅游协会是由国内文化旅游产业高级人才、专家学者、艺术家、企业家、社会贤达及政府有关部门合作组成的行业组织，协会成员横跨文化产业、旅游产业、体育、商业、房地产业等。立足辽宁、面向全国，服务文化及旅游产业的不断融合，用文化力量来提升和推动文化旅游业的快速发展。　　协会性质：　　本会是以辽宁省文化旅游行业相关的经济领域的企业、团体和个人为主体自愿结成的非营利性社会团体。它是连接政府和文化旅游企业的桥梁和纽带，是文化旅游企业的知心朋友，是各级政府的参谋和顾问，经报请省工商联注册成立的省级文化旅游专业协会。　　协会口号：　　共识、奉献、提升、共赢　　协会宗旨：　　本会以服务为宗旨，宣传、贯彻、执行国家文化旅游产业相关法律、法规和方针政策及投资项目的对接。维护会员的合法权益，加强行业自律与交流合作;团结全省文化旅游行业相关组织，积极配合政府开展文化旅游资源的策划、设计、建设、保护、挖掘、开发、投资等工作;推动各种文化旅游业态的发展，提高文化旅游行业规模化、集约化、专业化水平;打造多家文化旅游企业上市，为辽宁文化旅游强省和全面小康社会建设做贡献。　　协会特点：　　本会坚持用文化引领旅游，用媒体宣传旅游，用项目带动旅游，用服务提升旅游，用品牌壮大旅游。它集创意设计、生产运营、项目投资、产业融合、营销创新、企业维权为一体，以推动旅游业与文化创意产业不断融合为己任，为我省文化旅游业的发展壮大而努力工作。', '105', '{"id":"1793","name":"2.jpg","path":"supply\\/20180723\\/05fee748c1b6460204abd0cf9bed13d8.jpg"}', '辽宁省', '210100', '567', '1', '0', '1', '沈阳美景环境艺术工程有限公司', '{"path":"default\\/20180723\\/eef78ad6732211c7f8f0181428af8cd6.jpg"}', '王强', '1', null, null, '/supply/116.html');
