INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (124, '“文旅研究、文旅策划、文旅规划”服务', '174', '规划设计', '策划', '投资运营策划', '　　我们提供城市、旅游及地产的策划、规划、景观设计、建筑设计;将定位理论及易经、易学运用在中国城市和旅游策划行业。我们的观点：旅游规划是策划出来的、旅游规划是用脚跑出来的、旅游规划是用心写出来的!　　服务介绍：　　我们提供规范的项目服务流程及全程的服务——　　1、项目开始：了解需求、 撰写项目建议书、 签订合同、 组成项目组;　　2、明确问题：反复沟通，清晰问题与解决问题的步骤、 项目规划;　　3、收集和分析信息：寻找事实、内部分析、外部分析;　　4、拟订规划：寻找解决方案、选择最佳方案、提出规划报告、制定行动计划;　　5、制定行动计划：协助客户实施行动计划、改进行动计划、对客户培训。　　成功案例：　　《第四师七十四团旅游发展规划》　　——形象定位：金木水火土 探秘大坡马。　　通过非五行意义上的“金木水火土”来概括七十四团的旅游特色和品牌形象，总结出七十四团旅游的发展方向为以边境体验、文化创意、避暑养生、生态休闲为主旋律的重视互动性、参与性、体验性的情景化旅游发展思路。　　《第四师六十八团旅游发展规划》　　——形象定位：国家稻田公园。　　以军垦稻作为文化依托，以山水田园为生态基底，以团场连队复兴为理念，以旅居田园乐活为主线，将六十八团打造成为具有集稻田农作体验、乡村田居度假、健康养生休闲、儿童艺术游乐于一体的国家稻田公园。　　《第四师七十六团旅游发展规划》　　——形象定位：奇乐山水、彩虹边塞 。　　奇：大地奇观、边境奇观、雪山奇观、民族奇观、战役奇观、河流奇观;　　乐：田园欢乐、民族欢乐、胜利欢乐、户外欢乐、草原欢乐、戍边欢乐。', '208', '{"id":"id15396704575870","name":"\\u5fae\\u4fe1\\u56fe\\u7247_20181016141350.png","path":"default\\/20181016\\/5c548424d577eae689023232ac59f598.png"}', '北京市', '0', '575', '1', '0', '1', '北京乾鼎至成旅游规划设计院有限责任公司', '{"path":"default\\/20180724\\/354d8bc8d55d6d07d26aec09fc31eab7.jpg"}', '李奎', '1', null, null, '/supply/124.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (125, '“城市定位、城市主题文化、特色小镇、主题小镇”策划规划服务', '174', '规划设计', '策划', '投资运营策划', '　　我们提供城市、旅游及地产的策划、规划、景观设计、建筑设计;将定位理论及易经、易学运用在“城市定位、城市主题文化、特色小镇、主题小镇”策划、规划服务。我们的观点：具有真正意义行业细分的特色小镇、主题小镇(第一或唯一)，才是可能成型、成长或成熟的特色小镇、主题小镇。　　服务介绍：　　我们提供规范的项目服务流程及全程的服务——　　1、项目开始：了解需求、撰写项目建议书、签订合同、组成项目组;　　2、明确问题：反复沟通，清晰问题与解决问题的步骤、项目规划;　　3、收集和分析信息：寻找事实、内部分析、外部分析;　　4、拟订规划：寻找解决方案、选择最佳方案、提出规划报告、制定行动计划;　　5、制定行动计划：协助客户实施行动计划、改进行动计划、对客户培训。　　经典案例：　　2014年为第四师六十九团香极地镇策划实施的《打造中国玫瑰第一小镇》、《香极地香料品牌规划》，将这个头年三化建设现场会上排名末数的团场一举送进一等奖的阵营里。　　2015年为第四师七十八团阿热勒策划实施的“太极小镇”定位，通过比肩战略，牢牢地绑定一河之隔的世界名城——特克斯，“八卦城太极镇，中华有美名”，将这个三化建设排名倒数第一的小团场推上了当年的三化建设的第一名。', '107', '{"id":"id15396705415614","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810161415253.png","path":"default\\/20181016\\/8e32fa210b374a36d4a508637a8a9020.png"}', '北京市', '0', '575', '1', '0', '1', '北京乾鼎至成旅游规划设计院有限责任公司', '{"path":"default\\/20180724\\/354d8bc8d55d6d07d26aec09fc31eab7.jpg"}', '李奎', '1', null, null, '/supply/125.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (126, '“城市主题广场”定位策划设计施工一条龙服务', '174', '工程', '施工', '景观工程', '　　我们提供城市、文旅及地产的策划、规划、景观设计、建筑设计;将定位理论及易经、易学运用在中国“城市主题广场策划定位设计”服务中。我们总结提炼的观点：世界广场功能的三个发展阶段；第一阶段：广场即市政广场市民广场、第二阶段：广场即城市窗口和城市形象、第三阶段：广场即旅游产品、城市产业。　　服务介绍：　　我们提供规范的项目服务流程及全程的服务——　　1、项目开始：了解需求、撰写项目建议书、签订合同、组成项目组　　2、明确问题：反复沟通，清晰问题与解决问题的步骤、项目规划　　3、收集和分析信息：寻找事实、内部分析、外部分析　　4、拟订规划：寻找解决方案、选择最佳方案、提出规划报告、制定行动计划　　5、制定行动计划：协助客户实施行动计划、改进行动计划、对客户培训　　经典案例：　　1、第四师七十八团太极广场：以“太极禅”元素展开设计，运用现代构成手法，营造传统禅意与现代时尚律动的休闲养生和运动的广场自然景观；应用太极的弧线曲线和禅林的元素，打造唯一、独特、品味、幽雅的传统与现代相结合的园林景观。　　2、第四师六十九团婚庆广场：用玫瑰做旅游，用旅游卖玫瑰。　　3、第四师六十八团稻香文化示范区：“稻作文化，欢乐金鲤”，结合基地现状与历史文化特点，以稻作文化为主线，将其打造成为拥有浓郁稻香文化、富有群众和游客参与性及良好城镇形象展示的品牌性、主题性和差异性的稻香文化示范区。　　4、第四师六十八团机关广场：“阡陌交通·鸡犬相闻”，六十八团以水稻种植为其特色种植产业之一，取“水稻田”其“阡陌交通”的肌理特征为设计元素，融入到设计中。', '124', '{"id":"id15396706232981","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810161416521.png","path":"default\\/20181016\\/1d708101c6a15223c50fae37615514b0.png"}', '北京市', '0', '575', '1', '0', '1', '北京乾鼎至成旅游规划设计院有限责任公司', '{"path":"default\\/20180724\\/354d8bc8d55d6d07d26aec09fc31eab7.jpg"}', '李奎', '1', null, null, '/supply/126.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (127, '“城市门户”策划定位设计服务', '174', '规划设计', '策划', '产品策划', '　　服务描述：我们提供城市、旅游及地产的策划、规划、景观设计、建筑设计；将定位理论及易经、易学运用在中国“城市门户策划定位设计”服务中。我们的观点：门户不是大门，门户是城市景观，门户是旅游地标，门户更是城市的眼睛，城市的精气神都在门户中展现和反映；一个门户就是一个故事。　　服务介绍：　　我们提供规范的项目服务流程及全程的服务——　　1、项目开始：了解需求、撰写项目建议书、签订合同、组成项目组;　　2、明确问题：反复沟通，清晰问题与解决问题的步骤、项目规划;　　3、收集和分析信息：寻找事实、内部分析、外部分析;　　4、拟订规划：寻找解决方案、选择最佳方案、提出规划报告、制定行动计划;　　5、制定行动计划：协助客户实施行动计划、改进行动计划、对客户培训。　　经典案例：　　1、《第四师七十四团景观门户设计方案》　　——中华五行塔：国门金戈、华夏木艺、烟雨楼台、国门灯塔。　　2、《第四师七十七团景观门户设计方案》　　——七七：运用现代雕塑的抽象理念，将兵团编号进行了放射性的升华。背景似红旗又如同开垦的一片片良田，迎风飘扬，象征兵团的精神。　　3、《第四师六十七团景观门户设计方案》　　——百冠泉：让捷仁布拉克名副其实地成为“泉水多的地方”；让城市“市在湖上，泉在城中”，街傍水岸，水追楼影，体现出真正的一个美丽如画的“水市泉城”。', '107', '{"id":"id15396706911931","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810161417481.png","path":"default\\/20181016\\/5037a9cffd6a0afcbdbd0c95430907aa.png"}', '北京市', '0', '575', '1', '0', '2', '北京乾鼎至成旅游规划设计院有限责任公司', '{"path":"default\\/20180724\\/354d8bc8d55d6d07d26aec09fc31eab7.jpg"}', '李奎', '1', null, null, '/supply/127.html');
