INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (129, '国家5A/4A级旅游景区申创服务', '179', '规划设计', '其它', '景区创A升A', '　　服务介绍　　提供全套的国家5A级、4A旅游景区提升工程设计服务和申报服务，包括：　　1、硬件配套设施设计：包括游客中心选址与建筑设计、内部布局设计、内部装饰设计;旅游交通设计，包括交通道路、停车场建设方案；景区标识牌、旅游厕所、换乘站、旅游服务点、安全设施等全面且具有落地性的设计方案。　　2、软件配套服务设计：包括景区文化提升策划、企业VI、员工服饰、官方网站、智慧旅游、管理体制、运营体制、宣传营销等策划或设计方案。　　3、申报工作系列服务：制作宣传视频、汇报PPT、申报白皮书、申报材料等;编制创建方案;指导省级和国家级评审汇报；指导二级材料的搜集整理等。　　4、创建现场指导服务：现场指导各项设施按照提升方案进行的实地施工，不下10次分别进行模拟暗访和明察，指出创建过程中存在的问题，并制作的详细解决方案。　　5、指导各级迎检工作：编制详细的各级别机构检查的迎检方案，配合景区的迎检工作。　　6、其他工作：缺少最新总体规划的景区，对原有规划进行修编或重新制作总体规划；景区用于推广品牌的活动策划；导游词的更新和编撰等。　　服务优势　　拥有国家A级景区专业化技术团队和各省旅游系统强大的支撑力量，熟悉申报流程和创建过程中的难点、重点，对历年国家5A级景区的申报形势均跟踪研究。　　成功案例　　苏州吴中环太湖旅游区、昆明世博园旅游区、徐州云龙湖旅游景区、永城芒砀山景区、西峡恐龙园-老界岭旅游区、铜仁梵净山景区、麻城龟峰山景区等。', '200', '{"id":"id15396605011354","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810161127304.jpg","path":"default\\/20181016\\/a1b2600ace36f0c2b098d269ee0893de.jpg"}', '上海市', '0', '579', '1', '0', '1', '上海会景景观规划设计有限公司', '{"path":"default\\/20181120\\/5d0a1bd489d750255292f5f356d88c19.jpg"}', '马丽娜', '1', null, null, '/supply/129.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (130, '控制性详细规划、修建性详细规划服务', '179', '规划设计', '规划', '控规,修规', '　　服务介绍　　根据《旅游规划通则》、项目地具体情况以及客户的现实需求，对规划地块进行详细的控制性规划或修建性规划，主要工作包括以下内容：　　1、为协助业主申报建设用地，详细划定所规划范围内各类不同性质用地的界线，并根据需要协调上级主管部门进行调规。　　2、划定用地布局，规定各类用地内适建、不适建或者有条件地允许建设的建筑类型。　　3、根据地块总体规划布局，规定各地块建筑高度、建筑密度、容积率、绿地率等控制指标，并根据各类用地的性质增加其它必要的控制指标。　　4、规定交通出入口方位、停车泊位、建筑后退红线、建筑间距等要求。　　5、对各地块的建筑体量、尺度、色彩、风格等进行详细规划。　　6、确定各级道路的红线位置、控制点座标和标高。　　7、进行景观系统、旅游服务设施既附属设施、工程管线、环境保护等方面的详细规划和设计等。　　服务优势　　拥有从业经验丰富的景观设计、建筑设计、工程管线设计、3D动漫设计等技术精湛的专业设计人员，在十数个省份均有成功的规划经验。　　成功案例　　张家界七星山综合服务区、沂水“天上王城”、滑县森林公园、宜昌锦绣天城国际旅游度假区、中国风水文化苑等。', '84', '{"id":"id15396606788790","name":"\\u5fae\\u4fe1\\u56fe\\u7247_20181016113015.jpg","path":"default\\/20181016\\/e09df46d597594f2a853f842a7388ae5.jpg"}', '上海市', '0', '579', '1', '0', '1', '上海会景景观规划设计有限公司', '{"path":"default\\/20181120\\/5d0a1bd489d750255292f5f356d88c19.jpg"}', '马丽娜', '1', null, null, '/supply/130.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (131, '区域发展规划、景区总体规划、景区总体策划服务', '179', '规划设计', '规划', '总规', '　　服务介绍　　在详细勘察项目地具体情况后，进行资源分析、市场研究、上位规划分析、定位分析、功能布局、项目规策划、旅游产品规策划、游憩方式规策划、基础设施规划、食宿设施规划、土地利用规划、景观规策划、商业模式规策划、旅游房地产规策划、运营实施规策划等，为区域/景区发展制定总体格局，和兼具前瞻性、落地性的详细实施方案。　　服务优势　　团队人员拥有省、市、县、镇、景区等多个级别数十套规策划经验，能对接业主需求，为区域/景区长远发展和落地实施制定蓝图。　　服务流程　　初步勘察→项目建议书→商务洽谈→正式考察→头脑风暴会→初稿→中期论证稿→评审稿→成果稿。　　成功案例　　凯里市、商丘市、揭西县、苏州吴中区、保定清西陵、株洲炎帝陵、吕梁苍儿会、正安县、社旗县、毕节纳雍县、固原六盘山、沈阳沈北新区、绵阳北川、保定阜平、南阳邓州、恩施市、新疆伊犁国际旅游谷、海南保亭国际养生度假目的地等。', '171', '{"id":"id15396609449961","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810161135331.jpg","path":"default\\/20181016\\/94a16e2aae00a81e5df5dff2f2d75368.jpg"}', '上海市', '0', '579', '1', '0', '1', '上海会景景观规划设计有限公司', '{"path":"default\\/20181120\\/5d0a1bd489d750255292f5f356d88c19.jpg"}', '马丽娜', '1', null, null, '/supply/131.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (132, '景区大门设计与施工服务', '180', '工程', '施工', '景观工程', '　　服务描述：　　挑战原创文旅，上门服务团队。　　上门策划创意设计，甲方不认可不取费(开发商需付路费与安排食宿 )。　　服务流程：　　1、当地文化调研；　　2、大门策划方案设计；　　3、大门深化设计(全套施工图：含建筑、结构、水电 )；　　4、大门材料样板制作、大门小样模型制作；　　5、施工组织设计。', '210', '{"id":"id15396587234370","name":"\\u5fae\\u4fe1\\u56fe\\u7247_20181016105756.png","path":"supply\\/20180725\\/f308803d6be5e3d5bc6b5cc299ea63e5.png"}', '广东省', '440300', '584', '1', '0', '1', '深圳市创意时代环境艺术有限公司', '{"path":"default\\/20180725\\/b3ee7ba396ba10edd472a66ca886dbca.jpg"}', '许建平', '1', null, null, '/supply/132.html');
