INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (134, '全人文体验目的地、景区的策划、规划、设计及落地实施服务', '181', '规划设计', '策划', '产品策划', '　　我们倡导：每一个环境空间都是有灵魂，有生命力的。　　我们以“细致、精致、极致”三致理念，通过超前创意活化，人文艺术提升，全面塑造旅游目的地/景区产品、形象、气质，让其更具美感，更具人文精神和人文情怀，更具体验和价值，焕发生机和活力，成为全域旅游核心内容。　　专注于全人文体验目的地/景区的策划、规划、设计及落地实施。形成了从开发策划及概念性规划、总体规划、控制性详细规划、修建性详细规划、建筑景观设计、环境空间设计、IP文创设计、营销推广、文化建设、人员培训等产业链式服务，希望适合全人文体验目的地/景区打造的项目合作。', '190', '{"id":"id15396584228302","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810161049339.png","path":"default\\/20181016\\/a7d1cc27233729bb6241be6f091c1c65.png"}', '北京市', '0', '585', '1', '0', '1', '麦道合一（北京）品牌策划有限公司', '{"path":"default\\/20180725\\/21e5c8598cae1097873e4b0ee0f5cf89.jpg"}', '尚明双', '1', null, null, '/supply/134.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (135, '城市策划服务', '181', '规划设计', '策划', '产品策划', '　　寻魂，筑就城市持久魅力　　城市，是个性独特、内涵丰富，富有灵魂的生命体。　　城市的灵魂犹如人的个性，没有灵魂的城市就像没有个性的人一样，不可能具有独特魅力。　　一座美丽的城市犹如一首悠扬的歌，有着自己的语言和符号，随处激荡着自己的灵魂，散发着独特的魅力，给人以遐想和情思。　　改革开放后，中国城市得到了飞速的发展，然而在高速的城市化进程中也出现了诸多问题。城市发展同质化越来越严重，城市间的差异越来越小，城市的个性正在消逝，城市面貌千篇一律，形成了“千城一面”的窘况。一座城市，如果没有内涵和个性，就如同一个没有灵魂的人。因此，寻魂成为城市发展的第一要务，有了独具特色的个性和魅力才能更好的发展经济，扩大城市影响力，提升居民的生活水平和幸福指数。　　城市策划内容如下：城市主题定位城市形象策划城市旅游策划城市产业策划城市营销推广城市招商策划　　经典案例　　海南澄迈旅游发展总体规划、新疆帕米尔高原总体规划、湖北恩施野三关旅游总体规划、河北冀州新城总体规划、辽宁绥中滨海经济区总体规划、辽宁绥中秦始皇行宫遗址公园总体规划、广东佛山南海区九江镇控制性规划、北京市顺义区民俗园顶层发展规划、内蒙古锡林郭勒盟西乌珠穆沁旗旅游发展规划，以及陕西泾阳、陕西彬县、山西左云、河南邓州、辽宁绥中、山东垦利、山西和顺、河北河间、河北宁晋、河北唐县、河南渑池城市主题定位与总体策划。', '105', '{"id":"id15396583675930","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810161049332.png","path":"default\\/20181016\\/e7a975c08d484fe5db6fe888809fced2.png"}', '北京市', '0', '585', '1', '0', '1', '麦道合一（北京）品牌策划有限公司', '{"path":"default\\/20180725\\/21e5c8598cae1097873e4b0ee0f5cf89.jpg"}', '尚明双', '1', null, null, '/supply/135.html');
