INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (165, '综合设计服务', '218', '规划设计', '设计', '景观设计,建筑设计,装修设计', '　　“一驻九栖　　探宜驻之所领略玄妙之处;　　于久栖居所尽享归属之乐。”　　一驻九栖作为公司的基础业务，打破分工明确的设计手段，将设计综合化融入我公司“新设计服务”的理念。在设计大环境下，进行一次打破时空差距、专业差异以及人群差别的实验。　　项目包含了产业园规划、商业综合体、高层办公、酒店民宿、学校改造以及住宅等类型，同时与多所甲级院技术咨询公司以及国外事务所合作，从事规划、景观、建筑等各类项目的综合设计与咨询工作。　　参与主持案例：胜记仓邢台华北商谷、胜记安达北方商谷、北京七十一中、烟台海军航空学院综合楼、烟台蓝海软件园、烟台黄金产业园总部、菏泽道中道生物科技产业园、重庆武隆民宿、德国德累斯顿新城住宅等。', '342', '{"id":"id15410378458310","name":"\\u5fae\\u4fe1\\u56fe\\u7247_20181101100332.jpg","path":"default\\/20181101\\/91426db162f4e4e5f52b7c56a405609d.jpg"}', '上海市', '0', '237', '1', '0', '0', '驻梦（上海）建筑设计咨询有限公司', '{"path":"default\\/20180731\\/03ed69e688bc42c69d01052426862763.png"}', '刘耀鸿', '1', null, null, '/supply/165.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (166, '老城区及建筑改造、养老社区设计服务', '218', '规划设计', '设计', '景观设计,建筑设计', '　　回巷　　回，老房之形　　巷，古居之情　　回巷之间存旧忆犹新容　　回乡之情似落叶忆归根　　回巷是公司的特色板块，主要涉及改造项目与老年项目。我公司着力于世界以及国内两大设计主题，自发进行理论研究与项目探讨。公司诚邀高校人才，作为公司学术顾问团队，以新视角审视设计思路，以新观念改良设计产品，以此板块为媒介，形成该研究方向的讨论平台，与社会各界设计师及研发人员互动交流。　　参与主持案例：德国啤酒厂改造、重庆石桥铺街区改造……', '183', '{"id":"2140","name":"1.jpg","path":"supply\\/20180731\\/9c4ab3f821d4d03e6c8620f44c9527c8.jpg"}', '上海市', '0', '237', '1', '0', '1', '驻梦（上海）建筑设计咨询有限公司', '{"path":"default\\/20180731\\/03ed69e688bc42c69d01052426862763.png"}', '刘耀鸿', '1', null, null, '/supply/166.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (167, '教育及专项旅游服务', '218', '泛旅游', '教育', '研学', '　　“正负零　　正负之间，零乃初本;　　设计之间，空为形态”　　正负零是公司的回馈板块。“创新与突破”作为我公司一贯的企业理念，不忘本来，吸收外来，放眼未来，我们将实现国外资源、技术与国内设计力量的融合共享。通过中外的碰撞，找到适应市场的新思路，通过交流找到中国设计的突破口。同时，针对日益庞大的留学生团队面临常年海外学习与对国内市场的陌生化问题，我们有针对性地放慢回国学子适应市场、熟悉产品的步调，将国内的经验引出国门，更快地让我们的产品理念走出去，吸引更多设计新生代与国外设计院的青睐与认可。　　我公司着力打造以“本真”、“初心”为服务理念的设计师团队，在产品设计之外，通过双向互动，扩大眼界、开阔视角、放大圈子，实现价值最大化。　　案例：主持德国包豪斯建筑学院在校研究生进行主题交流学术活动……', '162', '{"id":"2139","name":"1.png","path":"default\\/20180731\\/03ed69e688bc42c69d01052426862763.png"}', '上海市', '0', '237', '1', '0', '1', '驻梦（上海）建筑设计咨询有限公司', '{"path":"default\\/20180731\\/03ed69e688bc42c69d01052426862763.png"}', '刘耀鸿', '1', null, null, '/supply/167.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (168, '旅游度假区开发投资一站式服务', '119', '开发', '主题园区开发', '', '　　一、服务介绍　　汇聚智慧，以具体旅游投资为标的，围绕委托方比较优势，科学组织相关要素，实现旅游项目高回报;为委托方量身构建旅游投资标的、全程提供优质咨询服务为公司第一要务。公司以大型旅游度假区(面向政府)、小型的以森林为代表的特色旅游区(面向企业)、高速公路服务区为主要对象，提供研究、策划、规划、设计、建设、营运、投资全程“一站式”咨询服务。　　二、服务优势　　1、重庆工商大学旅游与国土资源学院的科研平台与技术资源。　　2、资源行业专家与优秀规划人才团队。　　3、旅游行业政策和发展模式的深入研究。　　4、完善的融资渠道及开发运营能力。　　三、服务流程　　业务咨询——实地调研与收集资料——分析评价——确定规划目标——方案设计——方案评价——方案优选——方案实施。　　四、成功案例　　旅游度假区：　　(1)重庆南川金佛山旅游度假区总体规划(2015-2030)　　(2)重庆梁平百里竹海旅游度假区总体规划(2015-2030)　　(3)黔江小南海武陵山乡旅游区总体规划(2009-2020)　　(4)重庆市大仙女山旅游区总体规划(2009—2020)　　(5)“天空之桥”户外运动国际旅游度假区概念性规划等。　　特色旅游区：　　(1)重庆巴南•大来山树屋旅游区开发全程服务，　　(2)重庆万盛溯月•楠竹山竹屋民宿旅游区开发全程服务，　　(3)綦江永新“上善若水”漂流旅游区开发一揽子服务等。　　高速公路服务区：　　(1)重庆渝蓉高速冷水服务区概念性方案设计，　　(2)重庆渝蓉高速围龙服务区概念性方案设计，　　(3)渝广高速•静观服务区概念性方案设计，　　(4)沪蓉高速•云龙服务区概念性方案设计，　　(5)平罗高速•平塘服务区概念性方案设计等10个项目。', '153', '{"id":"id15395940657276","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810151700276.jpg","path":"default\\/20181015\\/b40cb72495aab7071922b8c8358b07c8.jpg"}', '重庆市', '0', '628', '1', '0', '1', '重庆汇智旅游规划设计有限公司', '{"path":"default\\/20180801\\/9d87b141825ec664e6a5206995989c8f.jpg"}', '罗光莲', '1', null, null, '/supply/168.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (169, '古城古镇、城市休闲、乡村休闲策划、规划、设计服务', '112', '规划设计', '策划', '产品策划,营销策划,投资运营策划', '　　我们在专业服务上秉承用理性支持决策、创新激发价值的理念，力图为不同类型、不同资源、不同阶段的各类客户提供定制化的决策依据，执行指南。我们以休闲旅游度假产品、文化休闲产品与地产产品设计为特色，商业运营模式创新突破为核心，景观、建筑和游乐的创新设计为手段，为休闲度假与地产开发提供运作与建设方案。　　基于绿维文旅大平台，致力于休闲商业-休闲度假-文化休闲的全景化专业咨询团队。经过多年的积淀，分院业务主要集中于古城古镇、城市休闲、乡村休闲三个主要方向。团队致力于通过跨界整合，颠覆传统商业模式，创新休闲生活方式。精研商业数据，洞悉休闲消费行为，立足行业发展前沿。　　经典案例：　　1、嘉峪关凯旋国际文化旅游综合体　　2、曲靖外滩文化休闲商业综合体　　3、霍尔果斯口岸综合服务中心　　4、吉安庐陵文化美食城　　5、山东祝阿文化小镇　　6、浙江淳安鳌山村富丽乡村　　7、新疆鄯善蒲昌村　　8、贵州黄果树民俗度假小镇　　安徽九龙山国际运动休闲度假区', '127', '{"id":"id15395939221134","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810151658221.png","path":"supply\\/20180801\\/8f86e7ab429ae42fe346fbbc8686d158.png"}', '北京市', '0', '629', '1', '0', '1', '北京绿维纵横商业管理有限公司', '{"path":"supply\\/20180801\\/b7357e396f23b5a34483dc35bd5626e5.jpg"}', '王阿慧', '1', null, null, '/supply/169.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (170, '旅游度假区规划服务', '240', '规划设计', '规划', '发展规划,专项规划,总规,控规,概规,修规,详规', '　　1、服务介绍　　提供旅游度假区概念性规划、总体规划、控制性详细规划、修建性详细规划等服务。　　2、成功案例　　(1)江西易达旅游度假区总体规划及修建性详细规划　　江西易达旅游度假区位于江西省樟树市，以盐温泉养生度假旅游为核心吸引物。目前，该旅游度假区规划已落地，旅游度假区已建成营业。　　(2)台州绿心旅游度假区总体规划　　目前，台州绿心旅游度假区已成功创建省级旅游度假区。', '108', '{"id":"2252","name":"1.png","path":"supply\\/20180803\\/cd9638596b1ca1b1facf6eb9e86d5263.png"}', '浙江省', '330100', '630', '1', '0', '1', '杭州海纳睿哲旅游规划设计有限公司', '{"path":"supply\\/20180803\\/285eff09d74567a8eec7fbf2137942ff.png"}', '章志攀', '1', null, null, '/supply/170.html');
