INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (189, '文化旅游项目（全域旅游、乡村振兴）策划、规划服务', '266', '规划设计', '策划', '投资运营策划', '　　绿维同心圆文旅创新分院核心团队包括西北地区资深文化旅游策划运营专家、旅游规划设计专家、文化旅游项目建设运营管理专家，在文化旅游策划规划，文化旅游项目开发和文化创意、融资平台搭建等方面具备人才优势、资源优势和诸多实际操作成功案例。　　朱正东院长：《银川市旅游总体规划》、《银川滨河新区旅游战略策划方案》、《银川滨河新区旅游总体规划编制》，协调完成了银川滨河新区艾伊薰衣草休闲度假庄园、黄河军事文化博览园、华夏河图银川艺术小镇、黄海外滩公园、景城公园、天山海世界黄河明珠等文化旅游景区(项目)的规划编制工作。　　李成荣总顾问：曾发起成立西北风情旅游联合会，打造“银川国际汽摩旅游节”，组织“贺兰山岩画国际艺术节”，规划银川滨河旅游区，策划“驾越丝绸之路·中阿巴友好万里行”，成功落地运营“宁夏观光夜市”等。　　程天成技术顾问：《广东东莞奥特莱斯购物小镇》、《江苏镇江新民洲生态小镇》等，在《贵州遵义凤冈县全域旅游总体规划》、《中国佛岭书法小镇》、《西安工匠小镇》、《鄂尔多斯市鄂托克前旗敖勒召其音乐休闲度假小镇战略策划及总体规划》、《白银市靖远县田园综合体规划项目》等。　　分院定位　　基于绿维平台，创新思维，扩展市场，专业服务，为政府及企事业单位提供战略规划、旅游项目开发运营指导。公司致力于文化旅游融合发展，全域旅游及文化旅游金融综合服务，努力打造区域及国内外知名的文化旅游发展创新品牌企业。　　分院理念　　公司在项目规划过程中着眼打造特色文化旅游IP项目，关注旅游新业态新项目新趋势，实现概念策划、规划设计、战略规划、营销管理、投融资运作、区域文化、城乡建设、产业发展等多方面全方位规划，确保每个项目都成为精品和行业标杆。　　业务范围　　包括：文化旅游项目的开发；旅游资源和文化创意产业的策划、规划、设计、管理；文化艺术活动的组织策划、推广、传播等。形成了从开发策划、总体规划、控制性详细规划、修建性详细规划、乡村专项规划、智慧景区方案的全方位一站式全程服务。服务项目包括：特色小镇、田园综合体、乡村旅游规划设计、休闲农业园、旅游景区规划等。', '195', '{"id":"id15395898467016","name":"\\u6d2a\\u5e7f.png","path":"default\\/20181015\\/afb995966041b971482f7e313d9dd7dd.png"}', '宁夏回族自治区', '640100', '644', '1', '0', '1', '宁夏同心圆文化产业有限公司', '{"path":"default\\/20180808\\/6d4d863edf5699fdb87f393b9800531c.jpg"}', '朱正东', '1', null, null, '/supply/189.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (190, '文化旅游项目及景区品牌营销推广及大型节庆活动策划实施服务', '266', '泛旅游', '文化', '', '　　同心圆文旅品牌营销推广核心团队与北京航美公司及各大电影院线建立了战略合作，团队包括西北地区资深文化旅游策划运营专家、市场营销推广及大型活动策划专家，在文化旅游项目开发和文化创意、文化旅游品牌营销推广、大型旅游文化节庆活动创意策划实施等方面具备人才优势、资源优势和诸多实际操作成功案例。　　杨剑虹总经理：男，回族，1968年出生，现任宁夏同心圆文化产业有限公司总经理。先后在宁夏广播电视总台、宁夏旅游投资集团工作。在宁夏广播电视总台广告中心任职(副主任、主任)工作20年期间，承担完成了数百项企业广告业务；在宁夏旅游投资集团担任旅游发展公司总经理、大型活动部部长期间，策划实施国家级跨境友好交流主题活动——“驾越丝绸之路·中阿巴友好万里行”，成功落地运营年游客超百万的城市休闲旅游综合体——“宁夏观光夜市”。具备非常丰富的文化旅游品牌营销推广及大型文化旅游节庆活动策划实施经验。　　李华营销总监，女，汉族，1978年生。现任宁夏同心圆文化产业有限公司营销总监。先后在宁夏广播电视总台、鄂尔多斯鄂托克前旗文化旅游集团工作。在宁夏广播电视总台广告中心任职(科长、副主任)工作10多年期间，承担完成了上百项企业广告业务；在鄂尔多斯鄂托克前旗文化旅游集团工作期间，策划实施了自行车嘉年华、草原国际合唱节等主题节庆活动。具备非常丰富的文化旅游品牌营销推广及大型文化旅游节庆活动策划实施经验。　　杨文华策划总监，女，汉族，1990年生。北京第二外国语大学旅游管理专业硕士研究生，先后在宁夏旅游投资集团旅游发展公司从事旅游规划、策划工作2年，辅助策划实施了国家级跨境友好交流主题活动——“驾越丝绸之路·中阿巴友好万里行”；在鄂尔多斯鄂托克前旗文化旅游集团工作期间，策划实施了自行车嘉年华、草原国际合唱节等主题节庆活动。具备非常丰富的文化旅游品牌营销推广及大型文化旅游节庆活动策划实施经验。　　板块定位　　基于绿维平台，创新思维，扩展市场，专业服务，为政府及企事业单位提供品牌营销推广及大型文化旅游节庆活动策划实施等相关业务服务。　　业务范围　　主要经营范围包括：文化旅游项目的开发；旅游资源和文化创意产业的策划；文化旅欧节庆活动的组织策划、推广、传播等。', '304', '{"id":"id15409742847251","name":"\\u5927\\u6c99\\u5934.png","path":"supply\\/20180808\\/937c806e76e86e7d3d55bd0e52baf224.png"}', '宁夏回族自治区', '640100', '644', '1', '0', '1', '宁夏同心圆文化产业有限公司', '{"path":"default\\/20180808\\/6d4d863edf5699fdb87f393b9800531c.jpg"}', '朱正东', '1', null, null, '/supply/190.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (191, '同心圆文化旅游投融资服务', '266', '投融资', '投资公司', '', '　　2017年10月底，受深圳文化产权交易所委托授权，宁夏同心圆文化产业有限公司作为“文化产业板”业务的西北大区运营中心，在西北地区内从事“文化产业板”线下撮合业务及投融资服务业务。　　深圳文化产权交易所有限公司(以下简称“深圳文交所”)是以国家赋予的文化+金融先行先试的政策为导向，以国家关于创新驱动和构建多层次资本市场的战略为出发点，以服务于文化产业实体经济为立足点，以支持中小微企业进入资本市场为专注点，涵盖文化产权中的物权、非物权、人才产权全要素，覆盖文化企业资产处置、资本融通、股权交易、产品流通全产业链，囊括登记托管、招标发布、募集发行、挂牌交易、公开拍卖全交易手段，整合柜台交易、线下撮合、层级路演和线上交易全平台服务，建成立足深圳、面向全国、影响国际的国家级文化产权交易和投融资综合服务平台；同时，也是中宣部、中国证监会等九部委联合发出的《关于金融支持文化产业振兴和发展繁荣的指导意见》中确定成立的国家级文化产权交易所及投融资综合服务平台。　　“文化产业板”是深圳文交所根据中央和国家有关部委赋予的责任和国家级定位，依据国家区域性股权市场的政策，以服务于深圳文化企业做大做强和全国文化产业发展繁荣为目标，以服务于文化产业实体经济为指向，为文化类企业提供包括股权融资服务及不限于股权交易的全金融类和其他综合类服务，而设立的专属于文化产业的行业性资本市场和具孵化功能的投融资服务平台。同时为其他行业的创新创意创业企业、项目及特殊人才提供培育、孵化平台和投融资服务。目前，宁夏已有5家文化旅游企业在深圳文交所“文化产业板”挂牌上市。', '375', '{"id":"id15409736868801","name":"2.jpg","path":"default\\/20181031\\/409dd38853f4997643dd43acba49f81f.jpg"}', '宁夏回族自治区', '640100', '644', '1', '0', '1', '宁夏同心圆文化产业有限公司', '{"path":"default\\/20180808\\/6d4d863edf5699fdb87f393b9800531c.jpg"}', '朱正东', '1', null, null, '/supply/191.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (192, '定制旅游及研学旅游服务', '266', '运营', '研学', '', '　　公司依托宁夏、内蒙古、甘肃、陕西等西北区域以及西双版纳的特色文化旅游资源和产品，以及韩美林艺术馆等艺术文化资源平台，充分发挥公司团队在文化旅游项目的开发、旅游资源和文化创意产业的策划、文化艺术活动的组织策划、推广、传播等方面的人才优势、创意优势和资源优势，积极策划草原丝路、自驾天堂、雨林风情、大漠胡杨等特色定制旅游线路和产品，以及艺术研学旅游等特色研学旅游产品，为全国各地游客朋友提供多元化的定制旅游产品，为青少年学生提高艺术修养和文化素养提供有益帮助　　冯婷运营总监，女，汉族。曾经先后在贺兰山岩画景区、宁夏中旅、鄂尔多斯鄂托克前旗文化旅游集团从事旅游营销、策划、产品设计、接待服务等相关工作，具有较高的定制旅游产品策划设计能力及接待服务经验。', '183', '{"id":"2369","name":"3.jpg","path":"default\\/20181031\\/034d62b6be8219714de5152c59cda100.jpg"}', '宁夏回族自治区', '640100', '644', '1', '0', '1', '宁夏同心圆文化产业有限公司', '{"path":"default\\/20180808\\/6d4d863edf5699fdb87f393b9800531c.jpg"}', '朱正东', '1', null, null, '/supply/192.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (195, '美丽乡村规划与特色设计服务商', '270', '规划设计', '规划', '发展规划,专项规划,总规,控规,概规,修规,详规', '　　可为业主提供全面专业的乡村规划设计与其他特色服务。有别于常规设计院穿衣戴帽流程式工作，我司更注重产品的创新、文化的挖掘以及与产业的融合。明确把现代农业产业作为镇、乡、村庄规划的产业支撑。秉承“只做一流的乡村，不做三流的城镇”规划理念，提升村庄生态环境品质，构建产村一体的综合性规划。　　我司在规划层面，打造顶层设计——概念规划——方案设计——建筑改造——景观创意一条龙式服务模式，从大局出发，统筹产业发展，生态环境建设，立足区域发展，考虑产业、村庄、生态、文化、景观等多要素，促进农村三产融合，应用新发展路径，落实供给侧改革，引领消费新需求，探索示范带发展的新思维、新理念、新模式，示范引领现代特色农业产业发展。　　在技术层面：要分析区域资源环境，产业发展基础，结合文化旅游，生态环境保护的基础上，统筹区域发展，确定乡村规划总体定位，发展重点，提出建设性规划内容，确定农业产业发展重点，明确休闲旅游农业发展路径，提升农业科技水平，充分挖掘地域风貌，构建村镇产业支撑体系。　　总之，在保护生态环境，文化传承，挖掘村庄特色，统筹村庄居民点与农业发展布局，合理选择重点发展项目，打造以产业发展为支撑，以休闲、旅游为主线，以大地景观格局的生态环境为脉络的总体发展格局。在以往成功案例中，我司规划设计过新疆·奇台碧流河镇皇宫村村庄改造规划设计、浙江台州大陈镇梅花湾建筑风貌整治、贵州·遵义西三镇金鼎山镇总体规划等项目，并得到甲方的一致认可。', '527', '{"id":"id15409721893311","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810311549341.png","path":"default\\/20181031\\/5a85eb89d76aa29a4f0393fd7b8a9906.png"}', '北京市', '0', '645', '1', '0', '1', '北京意点旅游文化创意有限公司', '{"path":"default\\/20180809\\/789ccb37dd7c95def36f463695dc60e3.jpg"}', '马若峰', '1', null, null, '/supply/195.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (196, '文化古镇设计专家', '270', '规划设计', '设计', '景观设计,建筑设计', '　　可为业主在文化古镇旅游方面提供专业的咨询、设计及开发运营建议。针对古镇规划和建设我们拥有成熟的运营模式及经验，并构建了一套完整的服务体系，可向市场客户输出全方位的解决方案。　　秉承“旅游是公民权利，文化是历史遗产”的理念，为文旅赋能，给城市点赞，我们将充分利用成熟的建设运营模式和经验，发挥专业优势，与政府和企业共同实践“旅游产业+教育产业”的前瞻性规划，创建既是景区，又是休闲聚集区，还是特色小城镇极具特色的创建模式。通过观光吸引核、休闲业态聚集、泛旅游产业延伸、城镇化配套建设四个方面的发展，实现就地城镇化的路径。并跟随大区域的旅游格局、构建自身产业体系、呈现创新旅游产品，为游客营造富于独特人文价值的魅力空间，为城市保留更多历史文化记忆，助力地方政府进行城市提质和产业转型升级，带动城市活力。　　在发展过程中，我们始终以文化创意创新为动力。让文化创意创新代替单一的产业发展方式和发展链条，始终坚持“挖掘地缘历史—改善旅游生态—打造文化古镇—传承历史文脉—提升城市形象” 
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (197, '户外亲子休闲乐园打造专业咨询与建设服务商', '270', '规划设计', '咨询', '', '　　为业主提供专业旅游规划设计咨询服务，以户外亲子休闲乐园规划设计的产品为重点,突出项目附加值，起到聚集人气，拉动二次消费作用。此外，针对亲子项目的专项设计，我们拥有独到经验。一方面：一直致力于非动力游乐、儿童行为学心理、教育理论、公共艺术等专业领域的研究和实践，并认为在项目规划设计中不应是简单的产品堆积，而应是将挖掘出来的特有主题充分地和场地进行融合，甚至可以理解为项目量身定制。另一方面：在展现现个性化、创新性的的同时，我们也很注重中细节。在规划设计中，硬件设施的一流细节化、每一个服务细节的精益求精、以细节成就完美，全力打造适合家庭的高品质服务。　　一直以来秉承着“好玩好看有内涵、健康的活动空间有利于儿童成长”等设计理念，集亲子、互动、休闲于一体，打造高品质亲子互动休闲一站式服务，打造孩子的多彩快乐童年。　　成功规划完成的湖南湘都小花猪农乐园，被一致认为是湖南地区休闲农业类型项目的特色体验地。为了产品可以尽快落地实施，并产生收益回报，我们坚持不以重资产行为填充项目，注重以创意轻资产与活动植入为手段，整合多方资源(教育、研学)，从前期的顶层设计到后期的运营管理均有丰富资源。', '174', '{"id":"id15409718405022","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810311543452.png","path":"default\\/20181031\\/f0de7f0643b85b7e19af24d1d6876a81.png"}', '北京市', '0', '645', '1', '0', '1', '北京意点旅游文化创意有限公司', '{"path":"default\\/20180809\\/789ccb37dd7c95def36f463695dc60e3.jpg"}', '马若峰', '1', null, null, '/supply/197.html');
