INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (223, '乡村振兴、特色小镇、田园综合体、休闲农业等景观设计、建筑设计', '246', '规划设计', '设计', '景观设计,建筑设计', '　　一、服务介绍　　依据项目地文化和资源特色，设计独具地域特色的景观和建筑。　　二、服务优势　　(1)团队拥有10年以上的景观、建筑设计经验；　　(2)主题突出，能够形成标志性景观。　　三、服务流程　　服务咨询——项目考察——策划方案——方案确定——施工落地——落地跟踪　　四、成功案例　　老牛湾民俗街设计，吕梁汇文化景区大门、综合管理中心设计，清徐醋场景观设计。', '294', '{"id":"id15395870439764","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810151503392.jpg","path":"supply\\/20180810\\/d856ba90fda9cd0df2096fcd35c59d8c.jpg"}', '山西省', '140100', '662', '1', '0', '1', '山西汇景创游规划设计有限公司', '{"path":"default\\/20181015\\/68f299c7c8e85e3762731cc5a50dc132.png"}', '宋绿叶', '1', null, null, '/supply/223.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (224, '景区托管运营服务', '246', '运营', '管理', '内部经营', '　　一、服务介绍　　景区托管运营，成功推向市场。经过多年实地探索、实战运营已初见成效，已成功运营的景区为山西良户古村，正在运营山西千年旅游景区。　　二、服务优势　　(1)团队拥有10年以上的景区管理运营经验；　　(2)全套管理模式的输出，为景区带来人才、培养人才、留住人才；　　(3)为景区发展保驾护航。　　三、服务流程　　服务咨询——项目考察——运营方案——托管运营　　四、成功案例　　(1)山西高平良户古村　　山西高平市良户古村在2007年由中华人民共和国建设部和国家文物局公布为第三批中国历史文化名村。村中的玉虚观历史建筑群为全国重点文物保护单位，蟠龙寨侍郎府为晋城市文物保护单位。现辖良户、蟠龙寨两个自然村，村落总面积3.773平方公里，现有505户共1527口人。我公司2013年开始运营良户景区，经过五年的发展，良户古村村容村貌得到了极大改善，人民生活逐渐富裕，很多离乡的年轻人也逐渐回归家乡，参与良户的旅游发展。　　期间，我们举办了良户留守儿童夏令营、良户农乐园亲子活动、古村落保护发展大会等活动，设立“良户书院”，并举办“良户书院杯”系列大赛。“艺术良户”的目标，正在一步一步实现。　　(2)山西千年旅游景区　　千年景区位于山西省吕梁市信义镇，景区拥有千年水库、吕梁最高峰骨脊山、小东川河谷、亚高山草甸等，良好的自然环境条件，形成了“山峰环绕、碧水穿流”的壮美景观。我公司从2017年开始，参与千年景区的运营管理工作，主要做了以下工作：景区运营团队的组建及景区管理部门人员的确定；景区管理部门岗位职责的确立，景区运营目标任务的确定；景区内控、工作流程相关管理制度的编制和确定，形成明确的管理手册；景区对外宣传推广方案及活动的策划，明确景区全年活动方案；景区服务中心的正常运行、门票的售卖；景区相关经营业态模式的确定；景区千年好合大厅功能定位、启动及运营;新媒体的设计及推广工作等。目前已经具备开业条件。', '169', '{"id":"id15395872132230","name":"\\u5fae\\u4fe1\\u56fe\\u7247_20181015150633.png","path":"supply\\/20180810\\/2ad9d87d768e5f2bf3c275ddbca23196.png"}', '山西省', '140100', '662', '1', '0', '2', '山西汇景创游规划设计有限公司', '{"path":"default\\/20181015\\/68f299c7c8e85e3762731cc5a50dc132.png"}', '宋绿叶', '1', null, null, '/supply/224.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (225, '全类型景观设计服务', '265', '规划设计', '设计', '景观设计', '　　承接商业住宅景观设计，建筑综合体及政府公建项目景观配套设计，市政道路绿化及市政配套设施公园设计，田园综合体及乡村旅游规划设计。我们把商业住宅景观设计定位为打造居家宜居环境，归家便是度假，把生态环境带回家，在市政绿化设计中追求天人合一，尊重自然环境，尊重大自然的生命力，加以借景的手法，振兴乡村旅游，望的见山，看的见水，打造绿色生态农村旅游，记住乡愁，田园综合体设计也是我们这几年在项目中一直在追求探讨，磨合团队。通过我们景观团队对景观空间的推敲，对功能需求的分布，合理利用，达到节能，环保，价值使用最大化的景观工程项目。我们一直追求团队协作精神，打造一流的设计队伍，从前期方案到深化景观施工图，从态度上严格按照规范，细致的对待每个景观节点细节，后期服务，跟踪现场，争取最大化的和方案接近，达到满意的效果。　　合作承诺：　　为客户提供优质的专业的设计服务，设计成果符合国家相关规范 ，站在客户角度，实现项目最大化价值。', '361', '{"id":"id15395859611362","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810151445482.png","path":"supply\\/20180815\\/d13b340e42f235ccaed92fe00aa5cfd9.png"}', '广东省', '440400', '683', '1', '0', '1', '珠海市大经科技发展有限公司', '{"path":"supply\\/20180815\\/af382bca3507497ac253d5d5a3a75711.png"}', '杨映辉', '1', null, null, '/supply/225.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (226, '全类建筑设计服务', '265', '规划设计', '设计', '建筑设计', '　　致力于为客户提供优质的设计服务，内容包括：项目前期策划、总体规划、方案设计、初步设计、施工图设计等，项目内容包括城市综合开发项目、大型住宅、星级酒店、商业建筑、文化建筑、教育建筑、博览建筑、体育建筑等，创作具有代表性的优秀设计作品。　　一直以来，我们不断地提醒自己：最后的胜利者来自于最佳的创意，不要轻言成功，继续去寻找那些充满力量、富有深度的创意。只有它们，才能在这个设计世界中脱颖而出。这是我们一直秉持的设计精神，通过关注当代建筑与文化问题，结合超前的理念为未来的城市提供独特、有效的设计;并以东方的自然体验和现代科技为基础，使建筑成为与自然和谐相处的人造复合体，让生活在其中的人们感受建筑所具有的生命力。坚持充分理解业主和市场的需求，在方案前期同步融入项目策划研究工作，协助业主明确项目定位及经营开发理念。最大化地挖掘提升项目价值，力争为业主创造出高素质、高附加值、艺术化的个性产品。　　对每一个项目除常规方案、初步设计和施工图外，还在全程的各个设计阶段增加优化设计环节。 
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (227, '美丽乡村规划设计服务 田园综合体规划服务', '115', '规划设计', '规划', '发展规划,专项规划,总规,控规,概规,修规,详规', '　　我司提供“美丽乡村”规划设计!欢迎有需求的朋友联系！　　不是乡村，而是乡愁；不是民宿，而是美宿；国匠——田园生活方式设计家。　　一、服务介绍：　　国家宏观政策支持乡村发展，国家提高了旅游用地保障支持，休闲农业发展促进乡村产业融合，旅游扶贫工程聚焦乡村，新政策、新常态、新机遇都促使乡村旅游必须发展，乡村旅游势在必行。　　国匠通过塑造独树一帜的乡村品牌，构建合理可持续的乡村产品，设计落地精品的乡村景观，建设美丽乡村。　　二、服务优势　　4大核心优势：　　①浙江模式的输出：国匠文旅伴随着浙江乡村旅游从1.0走到4.0，对“浙江模式”具有深刻的理解和实际操作能力。国匠将以“浙江模式”的输出为方向，引领全国乡村旅游的创新发展。　　②精英操盘团队：国匠的核心成员是浙江乡村旅游的开拓者和领军人物，具有多年实践操盘能力，运作的案例皆取得了良好的市场效益。其美丽乡村案例多次被央视媒体报导。　　③市场导向的规划设计：任何旅游项目最终的买单者是市场。十多年的操盘经验，成就了国匠精准判断市场需求的能力，彻底颠覆了美丽乡村建设从“输血”到“造血”功能的转变。　　④强大的媒体网络：国匠文旅与中国旅游报、上海旅游情报、中国报业旅游联盟、中青旅等媒体机构具有广泛深入的合作，可迅速对操盘的旅游项目进行造势炒作，打造旅游IP。　　三、服务流程　　项目初步沟通——实地考察——沟通交流，了解诉求——提出初步思路——签订合同——规划编制——初稿汇报——中期修改——后期终稿提交。　　四、成功案例　　千岛湖九龙源精品民宿村　　湖南新邵清水全国生态文化村　　仙居县美丽乡村改造　　湖南醴陵隆兴坳省级示范村　　浙江瑞安五谷小镇美丽乡村　　湖南新邵村庄规划　　句容丁庄美丽乡村核心区总体策划及系统规划', '391', '{"id":"id15395856909550","name":"\\u5fae\\u4fe1\\u56fe\\u7247_20181015144113.png","path":"default\\/20181015\\/2d855e116bcc7b3d2a49d124056e100b.png"}', '浙江省', '330100', '573', '1', '0', '0', '浙江国匠旅游发展有限公司', '{"path":"default\\/20180817\\/42f3a07b07e00332e786dec73ca713c7.jpg"}', '刘玉芬', '1', null, null, '/supply/227.html');
