INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (231, '乡村振兴规划、乡村或景区文化旅游规划服务', '357', '规划设计', '规划', '发展规划,专项规划,总规,控规,概规,修规,详规', '　　服务介绍：　　旅途智库专业从事旅游景区规划、设计，从规划地点的自然条件、历史条件、文化条件、基础建设条件等方面着手，着眼于项目落地进行规划设计。　　在资料搜集、分析研究和方案成型过程中，重点系统思考了：游客为什么要来这里旅游？这里能给顾客留下什么样的深刻印记？如何才能让这里的旅游资源优势得到最有效的挖掘和最充分的运用？如何保障这里旅游的个性化而避免贪大求全？这里旅游的品牌内涵是什么？如何增加游客在这里各个景点的体验感、价值感、满足感？……　　旅途智库在规划方面，尤其对乡村文化旅游开发具有特别优势。休闲农业与乡村旅游是当今旅游发展方向之一，具有强大的生机和广阔的前景。旅途智库在发掘乡村景观景点同时，也极为重视文化的挖掘与保护开发，使乡村旅游不但有美景、有美食，更有文化、有故事！　　服务优势：　　旅途智库乡村文化旅游开发具有多个成功案例，构建了农户—政府—企业的优质合作模式，通过文化+旅游+产业的开发模式，使乡村旅游更具生命力，也是农户更快受益，政府更加支持，企业更多投入。　　乡村文化旅游作为国家重点实施的战略，如何去读懂、读通政策文件，如何去用好、用精政策补贴，是乡村文化旅游的重要抓手。旅途智库在政策解读、政策实施有独到见解。在旅途智库的多个成功案例中起到了极大的作用。乡村旅游开发的灵魂是文化的挖掘，旅途智库在文化的挖掘、整理、打造中不断引入新思想、新理念使传统乡村文化与新型潮流文化发生碰撞与摩擦，让更多人去喜爱、了解，也让乡村文化再次发光发亮!乡村旅游开发是支柱是产业的带入，单一的旅游产业不足以支撑乡村的发展，要配合以服务业、制造业、种养殖业、文创业等产业的带入才能使乡村旅游更具活力！　　服务流程：　　选择确定专家组→专家组进行旅游资源调查(考察)→起草文本、绘制图纸→征求意见→编制组修改→专家评审→规划部门审核→政府审批。　　成功案例：　　《汝州市全域旅游规划》、《大河堡美丽乡村规划》、《汾酒博物馆策划》、《娄烦美丽乡村一事一议》、《平遥南政村乡村振兴规划》、《平遥东游驾生态乐园规划》、《娄烦县河北村乡村改造规划》等。', '333', '{"id":"id15399258182834","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810191310074.jpg","path":"supply\\/20180827\\/219b9f67bee39b1f0cb84469d2f19dde.jpg"}', '山西省', '140100', '734', '1', '0', '1', '山西旅途智库企业策划有限公司', '{"path":"default\\/20180827\\/bcc652c9ebc61252e445aa126458d193.png"}', '畅兆焕', '1', null, null, '/supply/231.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (232, '景区运营管理服务', '357', '运营', '管理', '内部经营,地方关系', '　　服务介绍：　　山西旅途智库企业策划有限公司，用新理念、新思想、用全域旅游的发展模式去规划旅游目的地，真正实现“旅游+”的概念，用全域旅游的理念规划提升旅游目的地的定位，通过挖掘旅游目的地的文化内涵，丰富游客的参与度与体验度，打造目的地品牌，实现旅游目的地市场效益的大幅提升。　　旅途智库母公司，四季风旅游是一家成立于1999年的旅游公司，是国内业界有影响力的旅游企业，太原市5A级旅行社。现已在山西控股19家出境游旅行社，在全国拥有29家分子公司，2000多家营业部，并在美国、俄罗斯、捷克设立三家海外公司。公司以独具特色的企业文化为核心，在发展模式上不断创新。　　另外，通过集团云旅天下云服务平台聚集的大量游客数据和各类旅游资源，挖掘游客需求和带动旅游目的地联动。通过社群来运营C端用户，建立与旅游目的地长久粘性，通过社群需求开发旅游目的地优势资源，为游客定制旅游活动及产品，实现供给侧改革。　　依托于四季风旅游公司的强大客源保障，必将为景区发展带来新的引擎！　　服务优势：　　旅途智库母公司四季风旅游集团从业20余年，为太原市5A级旅行社，在美国、俄罗斯、捷克设有三家海外公司，在全国拥有29家分子公司2000多家营业部，在山西控股19家出境游旅行社，拥有大量忠诚客户和丰厚市场经验，可为景区引流提供高效保障。　　旅途智库以“互联网+”的模式为引导，以“融合文化、发现亮点、挖掘特色、打包运营”为运营宗旨，以“捆绑、互助、社群、活动”为战略方针，服务内容涉及：旅游IP打造、旅游营销、人才培育、文创产品开发、智慧旅游建设、旅游招商引资等景区运营管理服务。　　服务流程：　　项目对接→专家组进行旅游资源调查(考察)→起草文本、绘制图纸→征求意见→编制组修改→专家评审→景区服务落地→市场引流→后期跟踪管理。　　成功案例：　　《绵山景区营销》、《台骀山景区营销》、《汾酒博物馆策划营销》、《华夏古文明、山西好风微摄影大赛》、《运城旅游推广项目》、《太原全民万人健步走活动》、《大同国际旅游节》、《北武当山旅游推广项目》等。', '188', '{"id":"id15399257253711","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810191308291.jpg","path":"supply\\/20180827\\/cebc6a642f169db9f8b32e715b2d48d0.jpg"}', '山西省', '140100', '734', '1', '0', '1', '山西旅途智库企业策划有限公司', '{"path":"default\\/20180827\\/bcc652c9ebc61252e445aa126458d193.png"}', '畅兆焕', '1', null, null, '/supply/232.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (233, '文化创意梳理、文创产品开发等服务', '357', '泛旅游', '文化', '', '　　服务介绍：　　文化创意产业是以创造力为核心的朝阳产业，“它以高于传统产业24倍的速度增长，已经成为众多发达国家或地区的支柱性产业”。文化创意产品的开发设计是文化创意产业发展的重要环节。　　如何发掘好的文化创意？如何打造好的文创产品?这是文创开发中两个重要的环节。文化创意产业，是一种在经济全球化背景下产生的以创造力为核心的新兴产业，强调一种主体文化或文化因素依靠个人(团队)通过技术、创意和产业化的方式开发、营销知识产权的行业。　　文化创意产品作为丰富旅游体验和提升旅游档次的主要途径，同样扮演着非常重要的角色。不论是主题还是内容，形式或是载体与服务，文创产业在各个层面都可以和旅游产业形成亲密的互动。由此，既有的旅游形式也能得以丰富，旅游这样一个无形的体验，才能与有形的产品相结合，从而开拓出一片新的市场和新的资源。　　服务优势：　　文创，不仅是观察文创的表面繁华，更重要的是理解怎样创造整个文创价值链。只有理解透这里的人文地理、政策举措、产品研发、生产制作、消费市场特征等全价值链后，才能因地制宜地打造出最有特色的文创项目。　　如何打造我们的本土文创产品？只是旅途智库文创开发重点思考的问题。旅途智库总结四点文创要点：　　1、 差异化定位，寻找独特风格。　　2、 原生性生长，匹配生活方式。　　3、 场域化运营，培育产业生态。　　4、 年轻人心理，让传统文化得以传承。　　旅途智库有专业的文创设计团队，由来自原台湾艺术大学校长谢颙丞教授、品牌专家王建中老师亲自操刀，专注挖掘整理文创素材，引入文创团队，为景区的文创开发打造属于自己的独一无二的文创产品！　　服务流程：　　项目对接→专家组进行文创资源调查(考察)→总结归纳文创资源→设计文创产品→产品初稿→市场评估→产品测试→产品修改→产品上市。　　成功案例：　　《娄烦河北村云栖谷文创整理开发》、《平遥东游驾生态乐园文创开发》、《朔州大河堡文创开发》、《河南汝州文创资源整理》等。', '153', '{"id":"id15399256510541","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810191307131.jpg","path":"supply\\/20180827\\/47b994b4ee739730683733be64fcb85d.jpg"}', '山西省', '140100', '734', '1', '0', '1', '山西旅途智库企业策划有限公司', '{"path":"default\\/20180827\\/bcc652c9ebc61252e445aa126458d193.png"}', '畅兆焕', '1', null, null, '/supply/233.html');
