INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (238, '水上乐园咨询、设计、建设及运营管理服务', '169', '规划设计', '设计', '景观设计', '　　北京城市海景投资顾问公司始于1999年，主要从事人造海滨浴场和水上乐园的投资、咨询、代建、经营管理服务。　　公司从投资策划、规划设计、可行性研究、土建施工、安装、调试及运营为一体的一站式服务平台，成功打造了“北京城市海景水上乐园”、“山西晋城城市海景水上乐园”、“河南濮阳城市海景水上乐园”“湖南昭山城市海景水上乐园”等水上娱乐项目，其中北京城市海景水上乐园被列入2016年热门玩水景点的前5名，昭山城市海景水上乐园被评为国家4A级景区，并被列为湖南省级旅游必玩景区。', '472', '{"id":"id15399253001443","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810191300282.jpg","path":"supply\\/20180827\\/1d6c984bc2ea4527dbba0723d9897eaf.jpg"}', '北京市', '0', '738', '1', '0', '1', '北京城市海景投资顾问有限公司', '{"path":"default\\/20181227\\/9e0c94e329b4eeba20c54537616ef242.jpg"}', '康宁', '1', null, null, '/supply/238.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (239, '特色小镇策划及概念性规划服务', '75', '规划设计', '策划', '投资运营策划', '　　策划规划设计团队主干有着十余年的项目经验，在以旅游产业为主导的特色小镇板块有深入研究和独特见解，并形成了系统的理论体系和丰富的实践经验。目前已经参与多个特色小镇项目的策划规划设计与运营建设，并有多个成功落地的案例。　　1、《瓯江生态旅游景区总体规划》、《浙江丽水古堰画乡景区5A创建提升策划和实施计划》（国家级特色小镇，被评为最美乡愁艺术小镇）；　　2、《湖北武汉蔡甸景绿网红小镇策划规划和设计》（省级特色小镇、省级田园综合体）；　　3、《江苏徐州彭祖文博小镇策划及概念性规划》（市级特色小镇）；　　4、 《云南丽江美林雪山温泉小镇项目策划和概规调整》；　　5、《广西兴安灵渠古镇策划及概念性规划》；　　6、《同济堂(麻城)云沙州健康生态小镇总体规划》；　　7、《安徽滁州樱桃文旅小镇总体策划》；　　8、《安徽滁州池衫湖小镇总体策划》；　　9、《安徽滁州琅琊仙居谷侠文化特色小镇总体策划》；　　10、《湖南张家界白族文旅特色小镇总体规划》；　　11、《浙江丽水栝州水城特色小镇定位策划》。', '504', '{"id":"id15395822560119","name":"\\u5fae\\u4fe1\\u56fe\\u7247_201810151343574.jpg","path":"supply\\/20180829\\/ff517e3486d1d810662126e2e6860eeb.jpg"}', '北京市', '0', '749', '1', '0', '1', '北京绿维创景度假区规划设计有限公司', '{"path":"default\\/20181015\\/301d4c8c8c78b6fb8b1b14f8a65d9d52.png"}', '代莹', '1', null, null, '/supply/239.html');
