INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (244, '乡村规划、实施与开发服务', '124', '开发', '综合开发', '', '　　一、慢村的解读：　　慢村是乡村振兴与逆城镇化潮流契合点上的重要创新形态，慢村是度假区，又是乡村综合体，更是产业聚集区。它是一个可以让旅行者放慢脚步，悉心感受乡村特质的目的地;是一个生活品牌，其以“生活，还可以再慢些”为号召，以“慢村的时间，就是奢侈品的终极形态”为产品宗旨，打造一种融合乡村与城市的生活与生产方式。　　二、慢村三大内涵：　　1. 以“五慢”理念打造乡村生活方式；　　2. 保持村庄原貌与土地关系；　　3. 系统性消解农村发展与城市资本的对立矛盾。　　慢村“三变”：资源变股权、资金变股金、农民变股东。即将农宅、农地等闲置资产，智力、信息、服务等无形资源转为股权，并将政府扶持资金也转为股权，由农民持有，农民成为股东，共同构成乡村开发主体，成为利益共享者。　　三、慢村四方共建模式：　　慢村的开发建设，资本方投入资金、慢村策划慢村策划运营方提供I-EPC-O总服务、政府提供配套支持、村民以乡村合作社形式进行经营管理活动，四方明确权责，开发权、运营权、土地所有权等权属分立，各方各司其职，共同构建四方共赢的建设模式。　　四、慢村四方共建模式：　　1. 三产联动，形成乡村发展基础；　　2. 多元收益，提高乡村品质；　　3. 新乡民下乡，完成乡村社会更新。　　五、慢村的总体布局与产品设计　　慢村既满足独居人口的休闲度假需要，更要慢村常住人口的休闲生活需要。因此，在产品设计层面，既要有特色的乡村旅游产品、居住产品，还要有满足日常生活的超市、商店、餐饮、休闲等商业服务设施，幼儿园、卫生站、图书馆等公共服务设施，以及满足产业发展需要的创客空间、电商中心等企业服务设施。', '1724', '{"id":"2816","name":"11.jpg","path":"supply\\/20180829\\/be1b385caf169e081604c17e7759d5e1.jpg"}', '北京市', '0', '750', '1', '0', '2', '北京慢村旅游开发有限公司', '{"path":"supply\\/20180829\\/be1b385caf169e081604c17e7759d5e1.jpg"}', '董双兵', '1', null, null, '/supply/244.html');
