INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (248, '旅游规划及相关事务的多元运作服务', '274', '规划设计', '规划', '发展规划,专项规划,总规,控规,概规,修规,详规', '　　杭州卓越旅游规划设计院有限公司是一家专业规划设计单位，主要从事区域旅游发展总体规划、旅游景区控制性详细规划、旅游景区修建性详细规划、乡村旅游规划、新农村规划设计、景观设计工程、地方文化产业规划等业务。公司骨干人员涉及规划行业始于2004年，与浙江大学、浙江工业大学、同济大学等院校有长期合作关系。我单位项目专家与团队成员主要来自浙江大学、东南大学、中南财经大学、同济大学、浙江师范大学等高校，涉及旅游、中文、建筑、园林、城市规划、环境设计、旅游管理、文化创意等专业，拥有一支扎实的规划队伍。　　与业内一样，针对旅游项目的所需展开不同的规划类型服务：包括旅游项目的前期策划、总体规划、详细规划、修建性规划服务等项目单位所需的一切规划服务。同期根据项目方的所需还可以进行针对项目的前期招商及后续的运营代管理等；同时还包括景区旅游服务行业人员的培训及宣传服务，包括旅游景区文娱活动的策划，政府性旅游节庆的开幕式策划及整体节目的编排演，旅游展会的投融资(包括旅游展会前期策划、中期招展、嘉宾的邀约、主题论坛策划、及后期的布展到完成)；旅游景区标志标识的设计、地区性旅游logo的设计或面向大众的征集策划和评选；旅游景区民俗活动的策划开展等相应活动。　　至今已完成：《湖北襄阳汉江湿地旅游规划》、《上虞英台故里祝家庄景区规划》、《灵武市长枣文化博览园总体规划》、《杭州余杭区琵琶湾旅游总体规划》、《安吉中华茗园总体规划》、《湖州鹿山生态休闲园农业规划》、《南宁市壮乡文化风情园规划》、《浙江六横岛原住民传统文化研究》等诸多项目，目前正在编制《重庆市铜梁县安居古镇旅游开发规划》、《山东中昌田家村生态园规划》、《镇江市文化产业发展规划》等百余个项目设计。', '756', '{"id":"id15395754798900","name":"1.png","path":"default\\/20181015\\/f9650f43fcb03c90da5c3d3c5e895a00.png"}', '浙江省', '330100', '768', '1', '0', '1', '杭州卓越旅游规划设计院有限公司', '{"path":"default\\/20181015\\/076f1385c9284e968861a84bd26d22a8.png"}', '寿葛平', '1', null, null, '/supply/248.html');
