INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (252, '全域旅游总体规划服务', '120', '规划设计', '规划', '总规', '　　(一)遵循一大理念　　全域旅游发展理念与模式　　以全域旅游的发展理念和发展路径编制的一部区域发展旅游总体规划。　　(二)两大导向引领　　1、目标导向：　　遵循全域旅游示范区创建、旅游产业提档升级、经济社会转型升级三大目标导向。　　2、问题导向：　　严格对接国家全域旅游示范区创建工作导则、国家全域旅游示范区验收标准，对照标准寻差距、补齐短板抓提升、创建实施分任务。　　(三)创新规划体系　　在全域旅游打造中遵循一大理念、两大导向基础上，提出构建1+3+5+8+1全域体系，围绕国际化全域旅游目的地城市的一大目标，紧抓三大龙头项目带动，实施五大全域旅游战略，提升全域发展布局、全域产品创新、全域产业融合、全域项目建设、全域公服建设、全域环境提升、全域营销建设、全域体制建设八大抓手，落实全域旅游创建任务分工与实施方案。　　(四)成功案例　　2017年8月，我院编制完成《江苏省兴化市全域旅游发展总体规划》。　　兴化，是文化科技打造的全域旅游城市的典型。从全域旅游顶层设计、三年行动计划及实施方案、全域旅游工作宣传推广、国家全域旅游示范区的申报等多项工作。严格遵循国家全域旅游示范区创建导则及国家全域旅游示范区验收标准，对准江苏省全域旅游示范区验收标准，形成《兴化市全域旅游发展总体规划》、《兴化市全域旅游三年行动计划与实施方案》两大成果体系，对全域旅游规划及国家全域旅游示范区的申报具有重要的指导意义。　　在兴化全域旅游打造中遵循一大理念、两大导向基础上，提出构建1+3+5+8+1全域创新体系，围绕国际化全域旅游目的地城市的一大目标，紧抓三大龙头项目带动，实施五大全域旅游战略，提升全域发展布局、全域产品创新、全域产业融合、全域项目建设、全域公服建设、全域环境提升、全域营销建设、全域体制建设八大抓手，落实兴化全域旅游创建任务分工与实施方案。构建了全域旅游发展“兴化模式”，推动兴化旅游产业提档升级与经济社会转型升级，对兴化发展具有现实指导意义。', '244', '{"id":"id15395749034711","name":"3.png","path":"supply\\/20180907\\/7abe27976b897cada4aef5d2081cff2c.png"}', '北京市', '0', '779', '1', '0', '1', '北京全景智库科技有限公司', '{"path":"default\\/20180907\\/b033a66135aaf273e85dccbd8dd120cf.jpg"}', '耿浩', '1', null, null, '/supply/252.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (253, '乡村振兴、美丽乡村、旅游扶贫规划及各类策划、工程咨询等服务', '283', '规划设计', '策划', '产品策划,营销策划,投资运营策划', '　　我公司提供“旅游规划、乡村振兴规划、美丽乡村规划、旅游扶贫规划及各类策划、项目可研、工程咨询”等业务，欢迎有需求的各职能部门、单位、朋友联系!　　本公司承接项目均高于相关国家标准、规定等，我公司会做最充分的准备为阁下提供保质保量的服务，以理念和技术赢得您的信任。　　一、 服务介绍：旅游规划设计、实施考虑因素　　旅游规划是依托规划区域的旅游资源及内外部条件，对区域的旅游业要素进行优化配置和对旅游业的未来发展进行的科学谋划。其实质就是根据市场环境的变化情况和可持续发展的要求，对与区域旅游业发展有关的生产要素进行科学合理的优化配置的方案。旅游规划是一套法定的规范程序，是对目的地或景区长期发展的综合平衡、战略指引与保护控制，从而使其实现有序发展的目标。旅游规划是为旅游的发展设计的一个框架，所以这个框架必须是长期的、稳定的，必要的。规划为当地产业的发展布局提供引导性作用、要结合当地特色，量身设计，打造真正适合当地的规划。在规划前期要做好现场调研、资料收集、走访调查等工作。做一个好的旅游规划必须要确保能做到一下几点：　　1. 促进旅游业健康发展　　2. 启发和诱导功能　　3. 调整和整合功能　　二、 合作承诺　　我公司承诺，如果我们合作后，您将获得“旅游规划、乡村振兴、美丽乡村、项目可研、工程咨询”的如下内容　　1. 一份现场调研报告　　2. 一份规划说明书　　3. 一份项目预算单　　4. 一份后续技术支持服务保障单　　您的信任，是我们共创辉煌的开始。　　三、 成功案例　　甘肃平凉•麻武乡旅游扶贫规划　　甘肃庆阳•镇原县白马池田园综合体　　兰州什川•长坡村儿童环保主题乐园　　甘肃和政•长坡村儿童环保教育主题乐园　　甘肃临夏•和政县松鸣镇狼土泉村旅游扶贫规划　　甘肃临夏•临夏市枹罕镇王坪村旅游扶贫规划　　甘肃临夏•康乐县鸣鹿乡拔字沟村旅游扶贫规划', '603', '{"id":"id15395741874590","name":"1.png","path":"default\\/20181015\\/4afc1ebdb10b48f1408db19d5f930de0.png"}', '甘肃省', '620100', '790', '1', '0', '1', '甘肃文旅文化发展有限公司', '{"path":"default\\/20180914\\/e09b35c543af1077f094f14d1e120240.png"}', '高加俊', '1', null, null, '/supply/253.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (254, '特色小镇、乡村旅游、休闲农业、智慧景区项目的策划规划等服务', '167', '规划设计', '策划', '产品策划,营销策划,投资运营策划', '　　我们是一家新锐和颇具创新的专业规划设计团队，创始人团队曾在国内外顶级设计公司工作多年，在项目设计中我们有国际视野和多年的本土落地经验。　　多年来一直专注于特色小镇、乡村旅游、休闲农业项目的策划、规划、设计及落地实施。形成了从开发策划、总体规划、控制性详细规划、修建性详细规划、乡村专项规划、景观设计、民宿建筑设计、施工图设计、智慧景区方案的全方位一站式全程服务。我们的愿景是成为国际知名的最具创意的乡土旅游规划设计事务所，聚集一批有中西教育背景和创造力的不甘平庸的原创设计师在中国大地上挥洒我们的创意，用设计的力量改变乡村的面貌，让土地更有价值。　　服务项目包括：　　特色小镇、乡村旅游规划设计、田园综合体、休闲农业园、乡村游乐园、国家森林公园、度假酒店设计、旅游景区规划等。目前已经正式落成和正在设计的项目都具有理念的实验性。', '637', '{"id":"id15395740359721","name":"2.jpg","path":"default\\/20181015\\/a8b9f1bb50bed6cf54977f56b7c42c8e.jpg"}', '北京市', '0', '791', '1', '0', '1', '北京晓程文旅科技发展有限公司', '{"path":"default\\/20181015\\/f789f6912c3fab85f1c32583fde2c1aa.png"}', '刘晓静', '1', null, null, '/supply/254.html');
