INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (268, '文化旅游、特色小镇、田园综合体项目的产业内容导入、策划、规划', '686', '泛旅游', '体育', '', '陆道集团始终坚持“智慧引领创新、共筑城市美好生活”的使命，围绕城市产业和人居融合发展需求，在文化旅游、特色小镇、田园综合体、产业地产等核心领域，为政府和企业客户提供有竞争力的智能运动、模块建筑产品及实施运营整体服务，以及项目的顶层策划设计，助力实施了大批具有影响力的标杆项目。陆道集团和客户一起，服务城市的发展进程，已为包括政府机构和知名企业在内的500多家客户提供服务，业务范围覆盖全国30个省市自治区。我们为全国各地的城市导入智能运动、模块建筑等全球领先的产业内容，满足人们美好生活的需求。响应健康中国战略，【快乐运动中国计划】正在帮助人们享受高品质的快乐健身、快乐游乐的运动工作、生活和娱乐体验。我们提供健康中国大众体育的创新解决方案，包括创新型智能运动公园的建设与大数据运营，智能足球培训，智能足球赛事等业务板块。目前已在全国近30个城市建成近50个智能运动公园项目。坚持绿色发展理念，我们为客户提供模块化集成建筑设计、制造、安装全程解决方案与实施，目前已在全国多地设有多个建筑加工制造基地，并在20多个城市落地上百个项目。产品主要应用于景区配套、商业展示空间、创意办公、别墅民宅、临建辅房、高端定制等领域。陆道集团具备建筑综合甲级、城市规划乙级、市政工程乙级资质，为客户提供策划咨询、城市规划与建筑设计服务，并在文化旅游、产业园、教育、绿色建筑等领域具有独创性的技术特色。陆道集团以全球化的开阔视野，肩负起描绘和实施城市蓝图的重要使命和责任。通过经济、高效、生态的解决方案，努力在维护自然和社会环境可持续发展的同时，提升人们生活品质，创建城市美好生活。', '800', '{"id":"4939","name":"\\u5168\\u9f84\\u667a\\u80fd\\u8fd0\\u52a8\\u516c\\u56ed.jpeg","path":"supply\\/20181121\\/7242f36b72babf80a499481df80d188b.jpeg"}', '上海市', '0', '1176', '1', '0', '2', '上海陆道智城文化创意产业集团股份有限公司', '{"path":"default\\/20181205\\/8c3222b45a0d80bbba929f9fb0a967b4.jpg"}', '苏小兰', '1', null, null, '/supply/268.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (269, '用绿色模块化建筑为文旅项目打通一条新思路', '726', '工程', '施工', '房建工程,室内装修,农林工程,市政公用工程,施工材料,木屋,帐篷,移动厕所,门禁停车一卡通系统,公共广播系统,有线电视及卫星接收系统,LED大屏幕显示系统,多媒体会议系统,防盗报警系统,节能系统,消防联动系统,雨水采集系统,综合布线系统', '陆隽模块建筑，以高效、专业、科技、环保的理念，全面面向未来绿色建筑科技趋势和要求。与客户共同引领绿色建筑未来，创造环保健康生活。其三大核心产品——打包箱式房、集装箱式房、薄壁轻钢房，均具备环境影响小、低能耗造价低、适应性强可移动、质量易控建造时效快等优点。且三种产品均可实现与卫浴系统、空调暖通系统、人工智能、室内快装、绿色能源、厨房电气设备、雨水收集系统、污物回收系统等全产业链集成整合。打包箱式房——在工厂将厢房拆解为顶底框架，并集成好内外装饰，进行打包运输至现场进行吊、拼装。以轻钢龙骨和雕花装饰板及岩棉或者ALC板的组合为主要材料。是陆隽三种体系中现场施工进度最快，使用人工最少的建筑体系。集装箱式房——在工厂内完成整箱模块生产及内装，进行整箱运输，到现场组装，安装便捷快速。以薄壁轻钢保温装饰一体板和国际箱体特种金属板为墙体的主要材料，外观可安装玻璃幕墙、木纹装饰板等各种装饰材料。整体风格以体现工业风、金属感为主。外观简洁现代，可兼具保温、隔热、防潮、隔音、抗震等多项功能的集装箱式外观建筑体系。薄壁轻钢房——较多应用于农村自建房的房屋体系，以陆隽模块钢结构骨架为核心体系，次钢龙骨加钢网模片组成墙板结构系统。在内外两片钢网模片之间，填充以轻骨料发泡砼体，实现整体墙体板组建模块房屋的一种工艺制造方法。陆隽模块建筑拥有包括：·建筑工程设计 ·电气暖通和智能化 ·钢结构生产与施工 ·太阳能绿色能源设计安装 ·室内精装修设计和施工 ·建筑幕墙专业设计和施工·BIM设计 等专业力量。母公司陆道文创拥有建筑综合甲级资质及强大的建筑各专业技术团队，为陆隽模块建筑提供设计专业支持。目前在湖州、佛山、盐城、杭州、滁州均由自己的生产基地。2018年当年度在上海、东莞、宁波、阜阳、南阳、舟山、烟台、义龙等多地落成二十余项目。囊括体育场看台、项目指挥部、高端私人会所、售楼处等多个应用领域。', '469', '{"id":"5113","name":"{6FBFF110-9125-D970-C39C-A22779F03CA5}.jpg","path":"supply\\/20181127\\/2f822a7327c44d77ed6e69fe534eba5d.jpg"}', '上海市', '0', '1264', '1', '0', '0', '上海陆隽建筑科技有限公司', '{"path":"default\\/20181130\\/1890d3b66a41b72a19b266b0f415cf27.jpg"}', '鲁洋', '1', null, null, '/supply/269.html');
