INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (272, 'VR全景，VR视频拍摄', '852', '互联网+', 'AR/VR', 'VR历史再现,VR错峰展示,VR极限体验', 'VR全景拍摄+旅游：360°无死角呈现景区VR全景拍摄是通过广角的表现手段以及绘画、相片、视频、三维模型等形式，捕捉整个场景的图像信息，使用软件进行图片拼合与渲染，并用专门的平台进行播放，用一种多源信息融合的、交互式的动态视景和实体行为的系统仿真使用户沉浸到该环境中，在视觉、听觉、触觉等人类感官世界制造虚拟的真实环境，让使用者如同身历其境，达到一种独特的宣传效果。|VR全景拍摄+旅游功能实现VR全景拍摄和旅游结合，将给游客呈现一个360°无死角的景区，配上专业的解说，提高趣味性。同时，360全景拍摄还可以在朋友圈广泛传播，提高景区知名度。同时，全景VR里面可以加入景区门票购买窗口，游客在浏览的同时可以更方便的进行自主购票。|VR全景相对于传统的图片和视频传统的照片与视频宣传，被美颜的二维图片跟特效视频很难吸引到游客的目光，而VR全景展示，正是为旅游景区量身定制，可实现景区真实环境全景展示，将现实场景通过链接的形式真实还原到互联网上，或者通过二维码的方式打印在宣传单页上，还可根据需要在每一个场景中加入适当的景区资料以及景区文化介绍，把景区的优势通过每一处细节展示给游客，给游客一个更加真实的体验，加深游客印象，提高景区的游客量。', '608', '', '贵州省', '522600', '1365', '1', '0', '0', '贵州拾光视界传媒有限公司', '{"path":"default\\/20181205\\/2665274e6c03b70717b3b0f5da9b7b07.jpg"}', '孙超', '1', null, null, '/supply/272.html');
