INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (276, '服务于房.商地产及旅游项目，制作项目VR虚拟现实展示体验系统', '790', '泛旅游', '教育', '', '北京现代中欧软件开发有限公司，本公司VR3D虚拟展示体验系统，主要针对房地产公司售楼人员.商业地产项目，文旅小镇，博物馆，公园景区及各大产业园。通过我们产品的使用能让购房客户体验到房地产立体样板间的真实样子和效果，游客和观光客通过我们产品的观看，更能增加对项目及景区的关注度。客户通过观看咱们项目的VR，让客户更快更全面了解本项目,房地产项目增加客户购房的意向，商业项目的客户增加对商业商铺订购，公园景区的游客会更加方便快捷了解景区各个景点。我们产品对各个行业都能提升相关项目展示效果。提升房地产，商业地产，旅游地产项目出租率和购买率，提升房地产，商业地产，旅游项目客流量和关注度。', '1112', '{"id":"5873","name":"\\u68373.jpg","path":"supply\\/20181214\\/c503dfd687fd93681a67b78d218e0767.jpg"}', '北京市', '0', '1157', '1', '0', '0', '北京现代中欧软件开发有限公司', '{"path":"default\\/20181212\\/8dea44d364c5695e8c9f4b9a5ce0e376.jpg"}', '郑秀华', '1', null, null, '/supply/276.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (277, '心愿云-专注于景区互动营销赋能', '619', '互联网+', 'OTA平台', '', '旅游不仅仅是一个休闲活动，同时也是一次文化感受之旅，一次超脱日常环境的心灵之旅。当下全域旅游背景和旅游景区门票降价大潮下，中国80%的景区都有祈福、许愿、现场表白的场景。乘上科技通过科技和文化相结合创新，针对游客情感互动需求，为大型景区提供心愿表达、抽奖互动、文创商城、轻社交、活动门票、商业广告为一体的线下线上立体营销解决方案，有效地将游客与景区建立互动连接，提升景区文化IP宣传，促进景区二次消费。心愿云通过互联网科技创新，促进全域旅游发展为目标，通过互联网、LBS、智能互动终端结合微信小程序打造“心愿云-游景点签到领礼包”产品。我们通过在无锡市多家景区布置智能互动终端，为游客推荐精品多日游玩路线，游客在景点智能互动终端前打开手机扫码签到参与领礼包活动，并发布微心愿向往生活美好，根据推荐旅游路线预订后续景点行程，游客签到景点越多和天数越长奖品越大。该活动目标鼓励游客在无锡多日深度体验游，礼包奖品围绕“吃住行游娱购”来设置，奖品来自政府采购、景点和企业赞助提供，同时通过心愿云平台与景点线上线下联动，能为景区和企业提供更精准的品牌宣传和商业引流，增加无锡市旅游整体收入。全域旅游是供给侧改革，通过活动产生旅游轨迹和情感大数据，带来旅游目的地战略定位、精准营销与业态创新。活动主要功能有：智能互动终端、景点签到领礼包、心愿互动、抽奖互动、门票预订、景区导览、文创商城等功能。1.智能互动终端：适应比较复杂户外环境，防护标准符合IP54，工作温度-40°~ +70°，可根据景区历史文化和IP进行定制；2.景点签到领礼包：为游客推荐无锡市的游玩路线，鼓励游客多日深度游，促进全域旅游整体收入，大数据洞察游客全域旅游游玩轨迹，提升区域整体旅游服务水平；3.心愿互动：游客向往美好生活的心愿发布到景点终端，通过多种互动玩法将游客与景区联接起来，汇聚微心愿产生用户画像和情感大数据，促进全域旅游精准营销与业态创新；4.抽奖互动，鼓励游客现场参与线上抽奖活动，奖品为品牌企业和景区周边商家免费赞助，大大提升游客参与度，为景区和企业进行商业导流；5.门票预订，通过活动推荐优秀旅游线路，游客在游玩过程中更多地了解周边景区和景点，一键门票预订，提升全域旅游景区门票销售；6.景区导览，无锡是国家历史文化名城，游客可通过图文、语音了解景区历史文化和典故，提升深度游；7.文创商城，促进文创产品的销售，提升景区文化IP和文化创新；', '1815', '{"id":"5936","name":"4.png","path":"default\\/20181217\\/523c35e634e8add191f3b281e5168b79.png"}', '上海市', '0', '1168', '1', '0', '0', '上海乘上网络科技有限公司', '{"path":"default\\/20181212\\/2724becc256f53baecbc9bb80665228c.jpg"}', '凌云', '1', null, null, '/supply/277.html');
