INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (282, '景区全业态解决方案', '640', '互联网+', '大数据', '智慧营销,智慧服务,智慧管理', '我们针对目前景区的多业态不统一难管理等问题，提供了整套景区行业解决方案，实现了景区或平台的统一管理，为业态调整提供了强大的数据支撑，实现最优最好产品的推荐，大大加强了旅游产品的粘合度和体验感。', '2165', '{"id":"6463","name":"\\u5e7b\\u706f\\u724719.PNG","path":"supply\\/20190104\\/b5c38a01d06d5caa3b9b86da0c990523.png"}', '广东省', '440100', '1183', '1', '0', '1', '广州市兆翔智能科技有限公司', '{"path":"default\\/20181211\\/5d6febb6f0a479c635c6638e6b744a73.jpg"}', '李克友', '1', null, null, '/supply/282.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (283, '户外无动力游乐设施', '471', '规划设计', '规划', '专项规划', '郑州有趣游乐设备制造有限公司，是专业的无动力游乐设备制造企业，公司立足于国家重要的综合交通枢纽城市郑州市，服务于全国。 产品借助于大自然中的高山、悬崖、丘陵、大海、小溪、洼地和沙漠等自然形式，精巧地与游戏融合，呈现在自然式的游戏场中。例如：高山、丘陵、洼地等都是地形上的变化，这意味着地面不再平坦，进而带动了更多肢体活动与延展，冒险、挑战、刺激和更多意想不到的乐趣随之而来。	　　目前景区中适合多年龄段人群游乐的体验项目并不多，但基础设施非常完善，这是两个具有巨大反差的事实，造成这种现象的缘由是游客的需求发生改变，最显著的变化时是组团游变成了自驾游，观光游变成了休闲体验游，线下支付变成了移动支付。游客消费行为的变化，使得景区中休闲游乐设施成为这个领域急需补上的短版。	　　我们研究发现，在休闲度假类的旅游目的地，需要的体验项目必须具有融合自然的特征。打造自然式游乐项目，是我们创建公司的核心，为大众休闲度假创造更美好的体验——终将成为我们“有趣游乐”人的为之追逐奔跑的奋斗目标!　　我们在自然式游乐场所需的项目开发上，以天然木材为主体材料，因为木质游乐设施更亲近自然。我们的木质游乐产品分为两种形式的产品，一种采用锯材，做防腐处理(无毒化防腐)，另外一种采用原木，只选用木材坚硬，抗腐耐磨刺槐木，游乐设备的设计是基于树干和树枝的自然形状，打造出独一无二的经典游乐项目。', '2095', '{"id":"4515","name":"\\u6709\\u8da3\\u4e1b\\u6797\\u63a2\\u9669.jpg","path":"bus\\/20181103\\/c0a883a2ee769347e8911e1d88b28341.jpg"}', '河南省', '410100', '840', '1', '0', '0', '郑州有趣游乐设备制造有限公司', '{"path":"default\\/20181217\\/5ab374fbc314e2591d3980ce42fe42c3.jpg"}', '王鸽', '1', null, null, '/supply/283.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (284, '仿真智能机电模型，如仿真恐龙 动物 昆虫 各种异形生产设计', '668', '运营', '游乐项目', '', '自贡亘古龙腾科技有限公司（以下简称公司），成立于2007年初，坐落于恐龙之乡——四川省自贡市国家高新技术产业开发区，是从事新兴科技产品设计研发、制作、销售和对外展出的综合型科技企业，着重研发电动仿真恐龙、仿真动物及昆虫、恐龙化石及骨架、仿真远古植物、埋藏挖掘现场和行走恐龙服等仿真产品，现已成为行业内的领军企业。公司占地面积90亩（约6万平方米），现有员工200余人，已通过ISO9001质量管理体系认证，仿真恐龙产品系列已获得CE证书，公司被阿里巴巴评为金牌供应商和四川风云网商，连续多年被国家五部委（商务部、中宣部、财政部、文化部、新闻出版广电总局）评为国家文化出口重点企业。我们的产品适用于博物馆、科技馆、游乐园、恐龙主题公园、大型商场、城市广场、度假村等。我们在注重国内市场的同时，早已进入海外市场，且拥有独立出口贸易权，制作的系列产品已出口到加拿大、美国、爱尔兰、波兰、捷克、德国、巴西、阿根廷、沙特、俄罗斯、克罗地亚、土耳其、澳大利亚、日本、韩国、菲律宾、泰国等70多个国家和地区。分别在多伦多、温哥华、辛辛那提、芝加哥等地举办多次大型恐龙展览。', '969', '{"id":"6797","name":"\\u4e00\\u53f7\\u5de5\\u5382\\uff08\\u529e\\u516c\\u5ba4\\uff09.jpg","path":"supply\\/20190215\\/95efa6f25fec6739f03737377bdaa2c0.jpg"}', '四川省', '510300', '1211', '1', '0', '0', '自贡亘古龙腾科技有限公司', '{"path":"default\\/20181204\\/3dd7029db4c2a6c7dee58a640ea074b4.jpg"}', '甘志强', '1', null, null, '/supply/284.html');
INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (285, '景区乐园迎宾、休闲、夜游项目：大型高科技实景山水秀', '1491', '泛旅游', '文化', '', '水秀在整个园区中有着“开园迎宾表演”、“午间休闲表演”、“夜间闭园表演”三个用途。其中“夜间闭园表演”与整个园区的营收有着至关重要的作用 ，既可以延长游览时间，提升园区衍生品、饮食、住宿消费，还能带动园区周边的夜间消费。我司打造的水秀产品运用大型多媒体灯光技术、激光技术、喷泉技术、喷火技术、烟花系统、全系统智能控制技术、大型仿生机器人技术、影视特效制作、异形LED拼接显示技术，结合现场实地的山水景色，给游客带来美轮美奂、震撼无比、惊险刺激的现场演绎。我司致力于大型山水实景高科技演艺秀等创新特色产品的研发、制造及实施，面向文化旅游、文化展示、文化演艺等领域自主研发大型高科技文化体验装备，已形成完整的策划-设计-研发制造-调试安装一体化服务配套。', '1137', '{"id":"6867","name":"2.png","path":"supply\\/20190220\\/12a1899e6c992278a880bee37bdd5107.png"}', '江苏省', '320400', '913', '1', '1', '0', '江苏金刚文化科技集团股份有限公司', '{"path":"default\\/20181018\\/4a84f395269f360172f6842c408bec6e.jpg"}', '李绪然', '1', null, null, '/supply/285.html');
