INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (306, '2019中国（山东）国际精品旅游产业博览会', '4859', '运营', '其它', '', '为全面贯彻全省文化和旅游工作会议精神, 积极落实《山东省精品旅游发展专项规划 (2018—2022年)》工作要求，发挥展会的示范引领和品牌推介作用，经山东省文化和旅游厅、济南市人民政府研究决定，于2019年8月29日-31日在济南举办“首届中国（山东）国际精品旅游产业博览会暨文旅融合新动能发展峰会”。“首届中国（山东）国际精品旅游产业博览会暨文旅融合新动能发展峰会”作为全省首届、全国唯一的以精品旅游产业为主题的创新展会平台，得到了省文化和旅游厅和省精品旅游产业专班的高度重视，也是促进会成立以来，举办的首场重大会展活动。本届博览会以“精品旅游新动能 文旅融合新机遇”为主题，集中展示山东文化旅游形象，宣传推介文化旅游资源，大力培育精品旅游品牌。通过展览展示、信息交流、商务洽谈、产品展卖等环节，促进交流合作，提升产业水平，增强旅游交易，创建一个推动文化旅游发展的国际交流平台。【展会主题】 精品旅游新动能 文旅融合新机遇【组织机构】主办单位：山东省文化和旅游厅 济南市人民政府承办单位：山东省精品旅游促进会 山东省广播电视台 执行单位：山东尚合国际会展有限公司飞鹰传媒集团 【时间地点】 时间：2019年8月29—31日 地点：济南西部国际会展中心【展会优势】产业赋能——山东省政府印发《山东省精品旅游发展专项规划(2018-2022年)》明确到2022年，山东旅游产业发展的动能转换基本实现，精品旅游产业作为促进新旧动能转换的十强产业之一，产业升级大幕已启，6000亿+航母级新旧动能转换基金、1000亿+精品旅游产业、1000亿+文旅融合产业、5+5十强产业、300+优选推荐项目，干亿级市场等您抢滩。行业聚力——高端论坛、产学合作、技术交流，聚焦行业热点趋势，尽揽前沿理念，实现精品旅游+产业交流合作；引领山东精品旅游开启合作发展之路。 价值合作——深刻洞察参展商、采购商、服务商的个性化需求，搭建产业、产品、市场、资本的务实合作平台，精准对接政产学研金五大平台资源，融入山东文旅强省战略的绝佳契机。全媒报道——全国知名官方媒体、网络媒体、行业媒体全程跟踪，强大的网络集群，DSP智能投放，全方位重塑企业形象，立体化推广品牌优势，构筑永不落幕的网上展会，一次参展，永久服务。【参展范围】规划展览面积30000平方米，标准展位1200个，共设置文化旅游新动能成果展示馆、精品旅游产业创新馆、国际旅游合作交流馆三个主题馆。 （一）国庆70周年——山东文旅融合发展新动能成果展示馆 （二）国际旅游与友好城市形象馆：1、国际旅游机构形象展区2、美丽中国城市旅游展区3、旅行社/在线旅游OTA展区 （三）精品旅游产业馆：1、精品旅游示范基地展区2、高端海洋旅游展区3、精品乡村旅游展区4、研学旅行展区5、体育旅游展区6、康养旅游展区7、精品民宿展区8、智慧旅游展区9、旅游商品展区10、非遗文化与演艺展区【参展费用】本届展会设国际标准3M*3M展位，每个展位包括：三面展墙，220V/5A电源插座一个、洽谈桌一张、两把椅子、两只射灯、中文楣板一块；特装空地不包含电源、桌椅；大会提供展馆保安、清洁服务，特殊要求另计收费。标准展位： 7000元人民币/个 特装空地： 800元人民币/平方米【高峰论坛】本届博览会将同期举办“中国（山东）文旅融合新动能发展国际峰会”，并设立6个分论坛：1、中国（山东）文旅产业投融资高峰论坛2、中国（山东）乡村旅游振兴发展大会3、中国研学旅行高峰论坛(山东)4、山东康养旅游发展论坛5、山东民宿文化节暨旅居产业高峰论坛6、中国（山东）智慧旅游创新峰会【组委会联系方式】电 话：0531-88529821 88529829 联系人：许飞 李杰传 真：0531-88529856 88529801 邮 箱：sdjplbh@163.com官 网：www.jplyblh.com       ', '3564', '', '山东省', '370100', '15598', '1', '0', '0', '中国（山东）国际精品旅游产业博览会组委会', '', '许飞', '0', null, null,