INSERT INTO lf.lv_service (id, name, user_id, serve_type, resource_type, product_type, summary, post_hits, thumbnail, province, cities, corp_id, status, recommend, co_count, cname, logo, uname, cstatus, istatus, iid, lfurl) VALUES (338, '宋城演艺AR+导游导览增加现实', '395', '互联网+', 'AR/VR', 'AR场景还原,VR历史再现,VR错峰展示,VR极限体验,AR智能导览,AR户外广告', '宋城集团，中国大型文化集团。旗下的宋城演艺是中国演艺第一股、全球主题公园集团十强企业，连续九届获得“全国文化企业30强”。项目背景中国大型文化旅游综合体打造者”的宋城集团，也在不断思考：在当前和未来一段时间内什么形式的信息化组织模式和空间集聚能够有效地将品牌营销、服务创新、知识溢出等要素融入其文旅产品中，以发挥其规模效应，为游客提供更好的体验和差异化服务？增强现实（AR）技术因此被宋城集团纳入应用方案。引入增强现实打造AR智慧导览平台增强现实正在重塑景区与游客间的互动和关联。在宋城项目中，视+AR（AR解决方案经理：成章vx：mei-ti-）深入宋城景区调研，与宋城挖掘出AR导航、导览、导游、导购、营销、游戏等诸多旅游应用场景。了解到这些应用场景和AR技术的未来发展方向，宋城集团从长远考虑，计划打造可持续运营、不断升级的宋城AR智慧导览平台。经过深入沟通，双方最终确定推出宋城导览APP，并以AR导游、导览为第一期落地应用场景。通过宋城导览APP，游客可以随时随地进行导航定位、AR场景互动、游玩路线规划、演出时间表获取、在线轻松预订门票/酒店、一键查看景区游玩攻略、购买 “宋礼”衍生品等众多惊喜体验。宋城导览在平台技术、架构、功能方面，宋城AR智慧导览平台以“分期搭建”为总体建设原则，第一期以实现基本AR能力和基础的AR虚拟导游、导览功能为目标，后续将通过平台迭代升级增加物体识别、SLAM、AR Cloud等能力，满足景区不同阶段运营需求。在内容方面，第一期内容结合了宋城自有IP形象小青、小僵尸、岳小飞等等，可爱的人物风格设计受到大众喜爱。宋城导览AR导游形象宋城导览APP目前已上线各大应用商店，用户下载安装宋城导览APP后，打开APP点击“AR扫描功能”，选择喜欢的导游形象后即可进入AR导游功能。AR导游可实现虚实交互的讲解过程，让宋城景区文化信息生动起来，以此替代传统的纸质、文字、真人导游介绍方式，使游客能更自由、更舒适的进行深度游玩。此外，宋城导览APP还拥有实时地图导览、语音讲解、游线攻略等多种功能模块，大大提升了游玩体验。景区AR空间导览虚拟景观互动游戏历史场景重现纪念品商店导购', '1175', '', '上海市', '0', '15638', '1', '0', '0', '视辰信息科技（上海）有限公司', '', '成章', '0', null, null, '/supply/338.html');
